(window.webpackJsonp=window.webpackJsonp||[]).push([[791],{1152:function(v,_,p){"use strict";p.r(_);var n=p(14),t=Object(n.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("blockquote",[_("p",[_("strong",[v._v("杨福家教授谈“大学之道”"),_("br"),v._v("\n文/潘星华（2007-02-25）")])]),v._v(" "),_("p",[v._v("英国诺丁汉大学校监、中国宁波诺丁汉大学校长杨福家教授谈大学之道时说：“大学之大，不在于校园大、校门大、院系大，而在于它凝聚了一流的人才，所展现的特色、独有的发展方向、大度、大气、自由和宽容的学术氛围。”")]),v._v(" "),_("p",[v._v("他去年有机会和中国总理温家宝见面，温家宝对中国大学没有特色，大学素质不高，感到忧心忡忡。")]),v._v(" "),_("p",[v._v("杨教授说：“大学要发展特色，的确很重要。名列世界大学前五名的美国普林斯顿大学只有学生6670人，其中研究生1900人。它既没有商学院、法学院，也没有医学院，却培养了25位诺贝尔奖得奖人，其中17个是物理奖；这就是特色。")]),v._v(" "),_("p",[v._v("“世界有48位得到相等于数学诺贝尔奖的菲尔兹奖（Fields\nMedal），美国的23位得奖人中，就有12人在普林斯顿大学。发展物理、发展数学，就是普林斯顿的特色，就是它独有的发展方向。”")]),v._v(" "),_("p",[_("strong",[v._v("先有一流管理人员，后有一流教授")])]),v._v(" "),_("p",[v._v("培养一流教授靠的是一流的管理人员。杨教授感慨地说：“一流的管理人能用一流的人才，二流的管理人只能用三流的人才。大学成功与否，就看人的优劣！”")]),v._v(" "),_("p",[v._v("他举普林斯顿大学关爱一流教授为例。他说：“普林斯顿大学的约翰·纳什（John\nNash）教授在1950年以博士论文开创了世界新的领域——‘博弈原理’（game\ntheory），他之后患了精神病。普林斯顿大学没有因为他生病请他离开学校，而是把他留在校园，给予他莫大的关爱，终于他痊愈了，并在1994年为大学赢得诺贝尔经济奖。这感人的故事后来写成小说《美丽的心灵》（Beautiful\nMind），还拍成电影。")]),v._v(" "),_("p",[v._v("“普林斯顿大学另一位教授安德鲁·怀尔斯（Andrew\nWiles）于1985年（32岁）升任正教授后就消失了，8年没有发表任何论文，他埋头苦干、潜心研究，终于在1993年6月回到他的母校剑桥大学向几百位世界一流的数学家，宣布他成功解开了“费马大定理”（Fermat's\nLast Theorem）这困扰数学界长达360年的大难题，并因此获得了历史上唯一的菲尔兹特别成就奖。")]),v._v(" "),_("p",[v._v("“这两位一流教授所达到的成就，应该说，普林斯顿大学提供的大度、大气、自由和宽容的学术环境，也该记上一功。有多少大学能包容一位患精神病教授在校30年之久？有多少大学能让教授8年不发表一篇论文？普林斯顿大学有容乃大，兼容并包，让教授发光。如果教授每年都要完成发表多少篇论文的指标，他能发光吗？他只能凑数，做容易做的事，能做360年解决不了的事吗？”")]),v._v(" "),_("p",[v._v("杨教授再举康涅狄格州立大学校长在24小时以1200万美元（1848万新元）挽留华裔克隆技术专家杨向中教授留校的事例。")]),v._v(" "),_("p",[v._v("他说：“杨向中成功克隆一头叫‘爱美’（Amy）的牛后，很多美国顶尖大学纷纷向他招手。校长向他了解情况，问需要多少钱才能在校内建立一个世界级的克隆中心，把他留下。他说，他需要遗传分子生物学、胚胎研究、器官移植和干细胞等多方面的人才协助，加上研究费、盖研究室，总共需要800万美元（1232万新元）。校长听了后表示会在24小时内回复。结果校长在与校董会商量后，在不到24小时通过电邮、电话录音和送上亲笔信，告诉杨向中教授：校董会决定拨款1200万美元支持他。”")]),v._v(" "),_("p",[v._v("杨教授说：“处处把人放在第一位的时候，离一流就不远了”。")]),v._v(" "),_("p",[_("strong",[v._v("点燃每个学生不同火种")])]),v._v(" "),_("p",[v._v("美国哈佛大学前校长德里克·博克（Derej\nBok）教授于哈佛建校350校庆典礼上说：“哈佛最值得夸耀的，不是哈佛培养了6位美国总统，也不是哈佛造就了36位诺贝尔奖得主，而是哈佛使每一个进入校园的学生都发光。”")]),v._v(" "),_("p",[v._v("杨福家教授受访时，引述博克教授的话说：“是的，每个学生都是金子，都有他的潜能。大学的任务就是要让他们发光，绝不以分数论英雄。”")]),v._v(" "),_("p",[v._v("他说：“3000年前一个哲人说，‘头脑不是被填充的容器，而是需要被点燃的火种。’每个人的火种都不同，大学要有能点燃每个学生不同火种，能让每颗金子发光的能耐，只有这样，才能成功。”")]),v._v(" "),_("p",[_("strong",[v._v("开发学生潜能是大学首要任务")])]),v._v(" "),_("p",[v._v("要怎样才能点燃每个学生内心的火种呢？")]),v._v(" "),_("p",[v._v("杨教授说：“师生要多接触，导师才能洞悉学生的潜能、强项。宁波诺丁汉大学就采取了每班只有15人的小班制度，这在中国目前还是绝无仅有。我们也认为一个教授带60个研究生，是绝对带不好的。传授知识固然重要，但不是最重要。大学最重要的还是开发学生的潜能，点燃他们头脑中的火种。”")]),v._v(" "),_("p",[v._v("牛津大学、剑桥大学最为人津津乐道的是导师制和住宿学院制；师生朝夕与共，在热烈的讨论和激辩中，新思维不断涌现，新一代各领域的领袖，也这样纷纷冒起。近年为美国培养多位总统的耶鲁大学也表示是吸收了牛津剑桥教育的精髓，才有今天的成就。")]),v._v(" "),_("p",[v._v("有关师生关系，牛津大学就有导师说：“要不断向学生‘喷烟’，直到点燃学生心里的火苗！”因为太熟悉学生的缘故，他们甚至认为不需要靠考试来了解学生的能力。")]),v._v(" "),_("p",[v._v("哈佛大学有一位指导160个学生的导师对杨教授说他非常了解学生的情况，杨教授不相信，并随手在名单上点了一个中国学生的名字，这位导师马上如数家珍，把学生的情况告诉杨教授。")]),v._v(" "),_("p",[v._v("杨教授说，他在一次和中国总理温家宝的对话中，建议中国的大学要朝世界级水平进发，最少应该要多有一些50人以下的班级存在。")]),v._v(" "),_("p",[v._v("他说：“总理很同意我的话，他说，‘一师对一生’更好。他举李政道的例子说，李政道就是在一师一生、手把手的情况下培养出来的。”")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("宁波诺丁汉大学招生不只看成绩")])]),v._v(" "),_("p",[v._v("为了改变高考“一试定终身”的现象，宁波诺丁汉大学的招生，不仅看学生的高考成绩，也有凭着高中成绩，通过面试进来的。")]),v._v(" "),_("p",[v._v("英国首相布莱尔前年到北京访问时，与宁波诺丁汉大学师生进行卫星连线对话，大学派出两名最优秀的学生到北京和布莱尔见面。两名优秀生选出来后，才让校方惊讶地发现，一人凭高考成绩进来，一人是凭面试成绩进来。")]),v._v(" "),_("p",[v._v("杨教授说：“考试是衡量学生能力的重要指标，但不是唯一的指标。”")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("学生是金子，要让金子发光")])]),v._v(" "),_("p",[v._v("访问英国诺丁汉大学校监杨福家教授，他引述哈佛大学校长博克教授的话说，每个学生都是金子，大学的任务就是要让他们发光。")]),v._v(" "),_("p",[v._v("杨教授还提到一位非常了解学生的哈佛大学导师，在他面前如何如数家珍谈学生。")]),v._v(" "),_("p",[v._v("这两位哈佛人的话，让我想起另外一位哈佛人，数学诺贝尔奖—菲尔兹奖得奖人丘成桐教授。")]),v._v(" "),_("p",[v._v("丘成桐说，哈佛大学数学系有一年录取了一个北京大学最优秀的学生，但是当哈佛向北大核实学生的身份时，北大数学系26位最资深的教授，竟然没有人认识这名学生，最后问到一位副教授才能确定这名学生的身份。")]),v._v(" "),_("p",[v._v("他说：“原来有些教授教学生，从不见面，也不相往来，这倒是闻所未闻的稀奇事。”")]),v._v(" "),_("p",[v._v("杨福家教授引诺贝尔得奖人李政道在2002年撰写的《物理的挑战》一书中说：“培养创新的科学人才，必须要有好的导师和密切的师生共同研究的过程。这是省不了的，不能用网络、程序代替的。”")]),v._v(" "),_("p",[v._v("新加坡的情况是怎样呢？经常听到学生一句话是：“千万不要让老师认识我。”")]),v._v(" "),_("p",[v._v("怎么才不让老师认识呢？学生会告诉你：“成绩不要太好，品行不要太坏。不过不失的学生，没有人会注意。”")]),v._v(" "),_("p",[v._v("这样的话，教师的一生要流失多少金子？人力是唯一资源的新加坡要有多惨重的损失？")]),v._v(" "),_("p",[v._v("加州大学圣塔芭芭拉分校校长杨祖佑教授，虽然身为校长，每年仍然争取教书和带学生做研究。他说：“无论怎样忙，事情有多伤脑筋，只要进了课室3分钟，就像进入另外一个世界。教书的时候，我会把学生的名字用心记下来，经常叫他们的名字，能让学生集中精神，也有亲切感。下课后，我还会多逗留5分钟到10分钟，和他们闲话家常，拉进距离。有时高兴起来，还会跟他们回宿舍，一起吃晚饭。”")]),v._v(" "),_("p",[v._v("我们的中小学教师或大学教授，有多少人能“用心把学生的名字记下来，经常叫他们的名字呢？”")]),v._v(" "),_("p",[v._v("每天，我们的教育工作者，来去匆匆，赶着“把书教好”，但是，“把人教好”，是不是更重要呢？")]),v._v(" "),_("p",[v._v("当新加坡的学校不断向成为世界级学府多方努力的时候，身为教育工作者，对我们的最重要产品，认识有多少呢？面对学生的时候，我们是想方设法要把他们头脑的容器填满，还是要设法点燃他们每个人心底不同的火种呢？")]),v._v(" "),_("p",[v._v("丘成桐说：“只懂讨论第三流问题的老师，绝对培养不出能钻研第一流学问的学生。”")]),v._v(" "),_("p",[v._v("“一流的学生，二流的教师，三流的管理。”希望这种情况，绝对不要在我们的学校发生。")])]),v._v(" "),_("hr")])}),[],!1,null,null,null);_.default=t.exports}}]);