(window.webpackJsonp=window.webpackJsonp||[]).push([[797],{1157:function(_,v,t){"use strict";t.r(v);var p=t(14),n=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|  |")]),_._v(" "),v("blockquote",[v("p",[_._v("编者按：踏雪飞鸿的《文革劫难纪实》一文主要讲述的是文革67年在四川省乐山大武斗中的遭遇，为帮助校史编纂者和年轻的朋友们了解文革中四川的武斗是怎么回事？特转摘此文供大家阅读分析。")])]),_._v(" "),v("hr"),_._v(" "),v("blockquote",[v("p",[v("strong",[_._v("文革中四川省的武斗")]),v("br"),_._v("\n摘自仙人居的博文《说说当年的武斗》"),v("br"),_._v("\nhttp://hi.baidu.com/shoha/blog/item/214a64388b20912496ddd8ad.html"),v("br"),_._v(" "),v("a",{attrs:{href:"pic/img.bimg.126.net_photo_KEUItsJVimdgYGt4E0ZLnw==_431219664321540318.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_KEUItsJVimdgYGt4E0ZLnw==_431219664321540318.jpg",alt:""}})]),v("br"),_._v("\n江青是红卫兵武斗的支持者"),v("br"),_._v("\n在全国来说，四川武斗开始得早，规模也大。")]),_._v(" "),v("p",[_._v("成都一三二厂的造反派和保守派自1967年4月下旬以来就不断发生磨擦和小型武斗。5月6日上午，该厂造反派调集厂外造反派围攻厂内保守派，双方用砖头、棍棒武斗。当造反派围攻该厂保密生产区时，退守生产厂房的职工动用民兵武装的各种枪支，向围攻者开枪射击，打死48人，打伤127人。")]),_._v(" "),v("p",[_._v("事发第二天，中共中央即发出《关于处理四川问题的决定》，决定撤销李井泉中共中央西南局第一书记、成都军区第一政委的职务。宣布川大“八二六”、“工人造反兵团”为左派组织，而“产业军”等组织为保守组织。调原西藏军区司令员张国华任成都军区第一政委，并以他为首组织四川省革命委员会筹备小组，负责四川省的夺权事宜。")]),_._v(" "),v("p",[_._v("“五六”事件当时在全国影响很大，北京和各地造反派纷纷表态支持四川的造反派，有的还派人前往成都进行增援。前一阶段，在“二月镇反”中被压抑的造反派这时候普遍翻了身或正在争取翻身，成都一三二厂的枪声成为了全国造反派的重新崛起的信号。紧接着，又发生了四川乐山的大型武斗事件。乐山市城区两大派群众组织，比较激进的造反派组织名为“红色造反兵团”，相对保守的群众组织名为“四军五部”。1967年5月14日，两派群众组织在原中共乐山地委招待所发生剧烈冲突，此后两派先用棍棒后用枪支开始了长达50余天的大型武斗，给国家和人民生命财产造成严重损失，双方被打死103人，被打伤1500人。[1]")]),_._v(" "),v("p",[_._v("7月初，谢富治、王力为首的中央代表团到成都，支持“八二六”，压制“红成”，致使四川地区两大派的对立情绪更为严重。")]),_._v(" "),v("p",[_._v("7月下旬以后，造反派在“文攻武卫”的口号下，大肆抢夺解放军的武器装备和物资。仅泸县武装部的武器库就被造反派抢劫达21次，被抢劫步枪2481支，轻机枪115挺，重机枪12挺，手榴弹115枚，各种炮72门，子弹28.4万多发。以至从成都、重庆、宜宾、泸州、涪陵等地开始，全川武斗不断升级。[2]"),v("br"),_._v("\n四川武斗又属重庆最为厉害。")]),_._v(" "),v("p",[_._v("解放后，八家大型军工厂建在重庆，分别承担从枪支弹药到水陆两用坦克、四联高射机枪（为当时援助越南的先进武器）等一应俱全的生产任务。在武斗最惨烈的杨家坪地区，就有国营建设机床厂、国营空气压缩机厂等数家兵工厂。建设机床厂大量生产半自动步枪及四联高射机枪。空气压缩机厂生产水陆两用坦克及装甲运兵车。两厂比邻，又分别由不同派别控制。武斗开始后，很快大量武器散失民间。")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_c_dms9tDinlGIOWf574NFw==_5684950104625835077.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_c_dms9tDinlGIOWf574NFw==_5684950104625835077.jpg",alt:""}})]),_._v("\n武斗期间，经常可以看到这种武斗车")]),_._v(" "),v("p",[_._v("7月1日，重庆“八一五”派和“反到底”派在建设机床厂发生武斗，“反到底”派占领全厂生产区，该厂生产和保存的半自动步枪大量流散。此后，抢枪事件不断发生，重庆武器进一步扩散。[3]")]),_._v(" "),v("p",[_._v("1967年8月5日，在国营建设机床厂清水池发生了以强抢建设机床厂仓库为目的的“清水池”武斗。双方动用了坦克、高射机枪等杀伤性武器。武斗中死亡25人，伤人不计其数。一派控制清水池后，砸开仓库大门，数万枪支被哄抢，造成了空前的隐患。")]),_._v(" "),v("p",[_._v("8月8日，“反到底”派望江机器厂“金猴”武斗队为向建设机床厂“反到底”派运送增援物资，用改装“炮艇”三艘组成“舰队”，沿长江溯江而上，沿途与占据东风造船厂、红港（朝天门码头）、长江电工厂等地的“八一五”派武斗队交战，打死24人，打伤上百人，打沉“长江207”拖轮等船只3艘，打坏12艘，长江航运为之中断。")]),_._v(" "),v("p",[_._v("8月12日，重庆两派群众组织武斗队在沙坪坝区嘉陵厂开展大规模武斗，双方参战均在700人以上，支援者上万。动用了枪炮、坦克。至13日夜，双方死伤上百人，经济损失357万元。[4]"),v("br"),_._v("\n8月14日，长安机器厂和三钢厂两派武斗组织在嘉陵江大桥发生大规模武斗，并猛烈炮击上清寺，打死11人，伤多人，烧毁房屋数幢。[5]")]),_._v(" "),v("p",[_._v("8月29日至31日，重庆市北碚地区造反派武斗人员2000余人冲进解放军四0四团营区，抢走枪支3000支，轻重机枪49挺，炮16门，枪弹16万发，手榴弹8000枚，打死指战员2人，抓走18人。[6]")]),_._v(" "),v("p",[_._v("《中国共产党重庆历史大事记》记载：9月1日，周恩来听成都军区司令员梁兴初汇报说到重庆一个工厂因武斗一夜打了一万发炮弹时，极为生气，说：这是国家财产，我是很痛心的。[7]")]),_._v(" "),v("p",[_._v("梁兴初的汇报的数字可能有些夸张。据史料记载，曾任四川省革筹组办事组组长的郭一民在一次揭发、批判刘结挺、张西挺的发言中说：“8月份重庆武斗升级，达到最高峰，一个月内打了1万多发炮弹。”[8]")]),_._v(" "),v("p",[_._v("1万多发炮弹是个什么概念呢？据统计，文革期间全国被抢夺的各种炮弹总计390642发，收回炮弹294259发，也就是说文革中全国共消耗了96383发炮弹。而重庆一地即占全国总数的10分之一以上。")]),_._v(" "),v("p",[_._v("毛泽东对四川的武斗也有很深的印象。1968年7月28日，他在接见北京造反派“五大领袖”时几次谈到四川的武斗。在谈到北京高校中武斗时，他说：“你们要打，就像四川那样大打。四川那才像打，双方几万人，开大炮。”“你们想武斗，要打就大打，像四川那样。”“我才不怕打呢！一听打仗我才高兴。北京算什么打，四川才算打，双方几万人开大炮。”")]),_._v(" "),v("p",[_._v("引文依据:"),v("br"),_._v("\n[1]《中国共产党四川历史大事记》，第317页。"),v("br"),_._v("\n[2] 《中国共产党四川历史大事记》，第321页。"),v("br"),_._v("\n[3] 中共重庆市委党史研究室：《中国共产党重庆历史大事记》，重庆出版社2001年版，第99页。"),v("br"),_._v("\n[4] 中共重庆市沙坪坝区党史研究室：《中共重庆沙坪坝区党史大事记》第二集，1999年内部出版(渝内刊准印号：99-051)，第131页。"),v("br"),_._v("\n[5] 中共重庆市委党史研究室：《中国共产党重庆历史大事记》，重庆出版社2001年版，第99页。"),v("br"),_._v("\n[6] 《中国共产党重庆历史大事记》，第100页。"),v("br"),_._v("\n[7] 《中国共产党重庆历史大事记》，第100页。"),v("br"),_._v("\n[8]\n《中央办的毛泽东思想学习班四川班革命大批判发言之四·郭一民：愤怒揭发批判刘结挺、张西挺挑动群众斗群众，大搞武斗，大反人民解放军，营造独立王国的严重罪行》（中央学习班铅印材料）")])]),_._v(" "),v("blockquote",[v("p",[_._v("---|---")])])])}),[],!1,null,null,null);v.default=n.exports}}]);