(window.webpackJsonp=window.webpackJsonp||[]).push([[801],{1163:function(_,v,p){"use strict";p.r(v);var n=p(14),t=Object(n.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("p",[v("strong",[_._v("铁路提速的保护神——翟婉明教授"),v("br"),_._v("\n文/晏礼中")])]),_._v(" "),v("p",[_._v("引用2009年04月03日"),v("a",{attrs:{href:"http://www.eeo.com.cn/",target:"_blank",rel:"noopener noreferrer"}},[_._v("经济观察报"),v("OutboundLink")],1)]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_xx1SaRVnor9cDsFnBn80qA==_2606458284342175938.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_xx1SaRVnor9cDsFnBn80qA==_2606458284342175938.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("翟婉明，1963年出生，江苏人。1985年毕业于西南交通大学机械工程系，1992年获机车车辆专业博士学位。现任西南交通大学牵引动力国家重点实验室教授，国际车辆系统动力学学会（I－AVSD）学术委员会委员。国家有突出贡献的中青年专家，教育部“长江学者奖励计划”特聘教授，国家杰出青年科学基金获得者，国家“百千万人才工程”第一、二层次首批入选者，教育部首批跨世纪优秀人才，曾获第六届“中国青年科学家奖”。主要从事铁路车辆工程及轨道工程交叉学科领域的动力学与振动控制研究，主持和参加了40多项国家级和部省级科研课题，发表学术论文130余篇，出版专著两部。在国内外率先提出并创建了\n“车辆－轨道耦合动力学”新理论，开创了铁路大系统动力学学科新领域。他建立的“车辆－轨道统一模型”，在国际上被列为本领域四大代表性模型之一，得到广泛引用，他也因此成为国际车辆动力学学界的知名专家。该理论被成功应用于我国铁路近10个重点工程\n（产品）之中，解决了列车提速及秦沈快速客运专线建设中的一系列关键技术难题。")]),_._v(" "),v("p",[v("strong",[_._v("翟婉明第一次坐火车是去成都上大学")]),_._v(" 。")]),_._v(" "),v("p",[_._v("那一年，大雨连绵，宝成铁路塌方了，很久路都不通，他只能转搭去上海的火车从昆明绕。这一绕就是三天四夜。一天一夜后，他到了贵阳。那时他刚从农村出来，家里穷，买完火车票后，就剩了两块钱。他不敢买吃的，只能自己饿着，看别人吃。好不容易饿到昆明，他又发现，从昆明到成都是快车，快车票还要再加7块钱，即使一路没舍得买口吃的，他那两块钱还是不够。他眼前一片茫然，只能拿着西南交通大学的入学通知书去找警察。在警察的帮助下，他最终填饱了肚子，到了峨嵋火车站。出站时，他见到学校来接的人，这成为他人生中最幸福的时刻之一。")]),_._v(" "),v("p",[_._v("二十多年后，当他成为牵引动力国家重点实验室的教授，为中国火车的多拉、快跑、更安全不断做出卓越贡献时，这段第一次坐火车的经历依然是他最津津乐道的记忆。")]),_._v(" "),v("p",[v("strong",[_._v("发达国家未必比我们强")])]),_._v(" "),v("p",[_._v("经济观察报：当初为什么会学和铁路相关的专业？")]),_._v(" "),v("p",[_._v("翟婉明：我们那时候，报志愿时对所学专业是没什么概念的。考西南交大也许是因为小时候在我们老家靖江那个地方交通太不便利，只有一条公路线穿过县城，去哪儿都很麻烦，所以，我比较向往“交通”这个领域，当时也没想到以后会搞铁路。")]),_._v(" "),v("p",[_._v("经济观察报：在上西南交大之前，对火车有什么认识？")]),_._v(" "),v("p",[_._v("翟婉明：上大学之前，我就没有坐过火车。唯一一次见到火车还是中学时学校组织去常州玩，专门从天桥上远远地看了一下火车站里的火车，跟看稀奇一样。")]),_._v(" "),v("p",[_._v("经济观察报：大多数“长江学者”都是“海归派”，作为少数本土博士之一，你对此有何感想？")]),_._v(" "),v("p",[_._v("翟婉明：对，我是地地道道中国土生土长的科研工作者，没在国外念过一天书。能够出国开阔视野当然是好的，但如果把它作为唯一的模式，我想，这并不是一件让人高兴的事情。过去我们很落后，需要向国外学习，但现在我们有理由相信自己可以培养出优秀的人才。我从来没觉得自己这个本土博士比留过学的洋博士低一等。我也出过国，去的是丹麦，但不是去留学，而是去讲学，人家请我去搞系列讲座。")]),_._v(" "),v("p",[_._v("经济观察报：你从来没想过出国深造吗？")]),_._v(" "),v("p",[_._v("翟婉明：我也有去英国读书的机会，雅思都考了，当时雅思还不像现在这样有那么多人考，我考雅思的时候，整个考场上，考生才两个人。后来我又为什么没去呢？倒不是我拒绝出去，只是了解了一下情况，发现英国的铁路，无论是在“重载”还是“既有线”的需求上，跟中国都是没办法比的，他们那边一直在萎缩，而这个学科我们当时还是比较强的，所以，我就在国内读了博士，博士读完，又被评为“跨世纪人才”、“杰出青年”、“长江学者”，于是，任务越来越重，也就没有机会出国深造了。")]),_._v(" "),v("p",[_._v("经济观察报：跟发达国家相比，我们的铁路处于什么水平？")]),_._v(" "),v("p",[_._v("翟婉明：在铁路这个领域，发达国家也未必比我们强，因为中国铁路有独特的社会需求。我们在运输线上遇到的问题都比西方多，我们的地质条件也很复杂，有特殊的环境和条件，比如青藏铁路上有高原冻土，国外都没这种环境，即使有这种环境他们也不会去做这样的工程。")]),_._v(" "),v("p",[_._v("经济观察报：目前国外铁路的发展是什么样的状况？")]),_._v(" "),v("p",[_._v("翟婉明：美国铁路的发展已经到一定层面了，日本的新干线也已经很成熟了。事实证明，他们在铁路科技上也不可能再有什么太大的发展了。")]),_._v(" "),v("p",[_._v("经济观察报：既然他们已经发展得那么成熟，为何不直接把那些技术引进过来？")]),_._v(" "),v("p",[_._v("翟婉明：直接拿别人最先进的东西、买人家准备好的参数当然是最便捷的，但如果我们自己没有中间这个过程，那任何时候的发展都只能依靠别人，也就是说，别人发展咱们才能发展，别人不发展了，咱们就只能等着。")]),_._v(" "),v("p",[_._v("经济观察报：在这个领域的科研工作你是如何起步的？")]),_._v(" "),v("p",[_._v("翟婉明：1993年，我被铁道部评为\n“跨世纪人才”，得了30万元的奖金；1995年我被评为“杰出青年”，又得了20万元。这两笔钱对我来说太重要了，我的独立科研是从这两笔奖金开始起步的。科研工作的展开离不开经费，正是这两笔奖金，为我在这个领域的研究和稳定发展提供了可能性。后来，我十五年磨一剑，一直没有松懈，才得了这么多荣誉。我没有那种去兼个职、搞个公司或是担任个什么行政职务的欲望，拿了让人羡慕的资助，目标和任务就在那儿了，虽然寂寞，但也没时间寂寞，只能毫不犹豫地往前冲了。后几年，到了技术开发的时候，对于任何疑难问题，我心里都有数，这都要归功于我那七八年的基础研究。所以，科学发展的过程必须是有规律的，一步一步的。")]),_._v(" "),v("p",[v("strong",[_._v("中国的铁路是跨越式发展的")])]),_._v(" "),v("p",[_._v("经济观察报：你在这个领域刚起步的时候，国内的情形是怎样的？")]),_._v(" "),v("p",[_._v("翟婉明：我们当时的基础非常差，当时迫切需要改进局面，而且不需要太多高深的理论。对于我当时做研究来说，这个领域到处都是机会，舞台是很大的。")]),_._v(" "),v("p",[_._v("经济观察报：你的“机车车辆-轨道耦合动力学”理论体系是怎么创建起来的")]),_._v(" "),v("p",[_._v("翟婉明：早在1990年，我就感到长期处于超负荷运输是铁路隐患，机车车辆对线路运行的破坏，轮轨的冲击振动加剧，一旦发生事故，后果不堪设想。于是，在沈志云、严隽耄、孙翔等教授的指导下，我们开始研制计算机仿真系统，建立“机车车辆-\n轨道耦合动力学”理论体系。")]),_._v(" "),v("p",[_._v("经济观察报：整个研制过程中，让你印象最深的事是什么？")]),_._v(" "),v("p",[_._v("翟婉明：第一次编制出核心程序后，我以为大功告成了，可没想到，等待我的却是“车轮远离钢轨”的结局。程序中肯定存在错误，但错在哪儿，当时还不清楚。我们做了无数次的修改，结果都不尽如人意。我甚至怀疑过整个程序的框架，几乎想放弃。但如果放弃，此前一切努力就白费了。有一天，我忽然想到了是钢轨模态选取有误，才导致了数值发散现象。")]),_._v(" "),v("p",[_._v("经济观察报：在列车的实际运用层面，你们如何展开试验？需要经常在列车上吗？")]),_._v(" "),v("p",[_._v("翟婉明：我主要还在线下，关注列车的动力性能，测量车辆对路桥的震动影响。一般来说，我们做的都是考核性实验。列车和线路的匹配程度一般分为“优秀、舒适、合格、不合格”四个指标，比如说要将某一路段的列车时速提速到160公里，我们就给列车设置一些动力参数，通过线下测试和车上测试看这些参数满不满足安全性、平稳性、舒适性，如果满足，就算是实验成功，就可以推广运用了。")]),_._v(" "),v("p",[_._v("经济观察报：最近忙的是什么试验？")]),_._v(" "),v("p",[_._v("翟婉明：刚忙完京津城际高速铁路的实验，最高跑了394公里，运营时速是300公里。")]),_._v(" "),v("p",[_._v("经济观察报：这次的试验是怎样开展的？")]),_._v(" "),v("p",[_._v("翟婉明：这是一次高速铁路的试验，目的是为了准确评估铺设高速铁路无碴轨道轮轨动态下的安全性。这种新型的无碴轨道，是由混凝土轨道板铺设的，取代了人们熟悉的用石子堆砌的有碴轨道。我们先将黑乎乎的铁轨打磨得明亮，然后才能把薄薄的应变片贴上去，这些环节都要在试验前就绪。我负责的两个测区相距约十公里，但测试工作必须协调一致。我要来回在这两个测区跑，反复趴在地上和站起来，检查上百个测点的仪器布置情况。当试验列车呼啸而过，仪表记录下时速300公里，且运行安全平稳时，我们的试验才算成功。")]),_._v(" "),v("p",[_._v("经济观察报：咱们的火车能跑多快？提速还有多大的空间？")]),_._v(" "),v("p",[_._v("翟婉明：这个问题的确是老百姓比较关心的。从1997年的第一次大提速开始，经过了六次提速后，我们的时速已经从每小时80公里提到了260公里，其中2004年的第五次大提速受益面最广，提到了时速160公里。2006年第六次提速，只是在一些有条件的路段，将时速提到了260公里。这些都是在“既有线”，也就是老铁路上的提速。在世界上，“既有线”时速200公里已经达到极限，如果再高就违反科学规律了。因此，目前还没有第七次提速的规划。")]),_._v(" "),v("p",[_._v("经济观察报：我国现在铁路发展的现状如何？")]),_._v(" "),v("p",[_._v("翟婉明：我们的路网长度虽然只是美国的十分之一，德国法国的八分之一，但我们这十几年的客车提速和货车重载是跨越式发展的，以前的列车又慢又不舒适，服务也不好，现在列车的设计都很舒适，性能也很好。现在国家已批准建设10余条高速客运专线，其中就有四川的成绵乐客运专线。尽管如此，它也只能满足社会需求的60%。而在四川，由于山区多，虽说比前几年提速快，但列车运营速度还不够高，这让我们十分焦虑。只有不断攻关，才能改变现状，大上台阶。")]),_._v(" "),v("p",[v("strong",[_._v("科研的乐趣在于不断被同行认可")])]),_._v(" "),v("p",[_._v("经济观察报：不工作的时候你有什么娱乐活动？")]),_._v(" "),v("p",[_._v("翟婉明：原来我喜欢爬山，因为在成都有这便利，就办了张青城山的年卡，二三周去爬一次山，那是一种很好的锻炼。但是，后来工作越来越忙，就二三个月爬一次，而现在变成一年一次了，虽然我不抽烟不喝酒，但平时不锻炼，身体也是个问题。")]),_._v(" "),v("p",[_._v("经济观察报：工作这么忙，身心绷得太紧不好吧？")]),_._v(" "),v("p",[_._v("翟婉明：谁都想放松，但真的没时间呀。可能是人过了45岁，就特别想赶紧弄出点成绩来，而且必须赶它的时效性。所以，我们没有周末节假日，晚上基本上都是加班。不是我一个人，我们整个课题组都是这样，习惯了。平时也就是晚上加完班，大家一起出来吃点夜宵。")]),_._v(" "),v("p",[_._v("经济观察报：科研工作的确不易，可能还有很多人搞一辈子科研，却什么荣誉和成绩都没有。")]),_._v(" "),v("p",[_._v("翟婉明：没错，付出了很多心血，却得不到认可的人，要比得到荣誉的人多更多。")]),_._v(" "),v("p",[_._v("经济观察报：搞了这么多年的科研，你有什么感受？")]),_._v(" "),v("p",[_._v("翟婉明：我虽然头衔里有一个“副主任”，但主要工作还是带着课题组做研究，一直在一线工作。对于科学研究，我的体会是，搞科研要一步一步来，要有坐冷板凳的充分准备。我前七八年做基础研究时，非常寂寞。不过，对于搞科研的人来说，苦与乐都是交织在一起的，如果哪天不加班，我心里会觉得怪怪的，不正常。这不是一天两天，也不是一年两年，我在这个领域做了二十年了，别人都觉得这样的生活是辛苦的，可我不觉得，因为它已经成为我生活的一部分，而且我能从中感受到乐趣。")]),_._v(" "),v("p",[_._v("经济观察报：感受到什么样的乐趣？")]),_._v(" "),v("p",[_._v("翟婉明：如果我的研究工作是乱的，无法向前推进，对社会起不到积极的作用，得不到社会的认可，我心里就会很难受。我的乐趣就在于，不断取得一些进展，然后不断被国内和国外的同行认为是有意义的，在某个方向上有推进作用。")]),_._v(" "),v("p",[_._v("还有就是，我们是搞工程科技的，工程科技是和国民经济直接相关的，我前面七八年觉得寂寞，是因为基础研究还没机会运用到实际中，但当这个基础出来以后，剩下的工作就很实际。比如列车如何设计出来，要有一个方案，我们通过设计参数，在几十上百套方案中为工厂提供最优化的一种或两种参数方案，让工厂用于样车生产，如果没有这种仿真平台，不可能搞这么多方案，原来是选出三五个方案通过实验来比比看，事实上三五个方案也不能比较出最优的。再说个更具体的例子，有家机车厂生产的机车，别人定单下了，等着要用，却发现在100公里到150公里时，它摆动较大。当时工厂要求不能动结构，因为一动结构，生产线就得换，就得花一年的时间，而五六十台的订单，一台的定价是980万，这是好几个亿的损失。这是列车本身悬挂匹配的横向参数不合理的问题，因为我们的仿真平台已经有了，所以不用像原来那样到机车上去改参数，改车的结构，然后再进行实验，我们只花了一个多月的时间，在仿真平台上找出了几组敏感的参数，提了最优化的两套方案给他们，他们把参数一改，就达到了优良状态，批量生产了，现在这些机车是陇海线、京广线上的主力机车。这就是我们工作的乐趣。")]),_._v(" "),v("p",[_._v("经济观察报：实用性如此之强，你面临的诱惑不少吧？")]),_._v(" "),v("p",[_._v("翟婉明：出了名后，诱惑也有，一些研究单位给出了优厚的条件来搞所谓的“抢人才”，但我一直在西南交大，所以有很深的“交大情结”，谁挖也不想走。外面现在的社会风气我也不是很了解，我只知道交大的风气勤奋踏实，很适合教书育人和科学研究。")]),_._v(" "),v("p",[v("strong",[_._v("博士应该是在某一领域有独特见解和研究的人")])]),_._v(" "),v("p",[_._v("经济观察报：你对现在高校扩招有什么看法？")]),_._v(" "),v("p",[_._v("翟婉明：现在各个大学扩招得都很厉害，能让更多人有受教育的机会，能提高全民素质，这是没错的。但在研究生和博士生的扩招上，我觉得国家似乎还是应该有些调整。我当初在西南交大读书时，研究生每年招七八百，可现在一年能招两千七八百人，除此之外，我们国家的博士总量在世界上也许也是最多的。")]),_._v(" "),v("p",[_._v("经济观察报：研究生、博士多一点对国家不好吗？")]),_._v(" "),v("p",[_._v("翟婉明：不是不好，多了质量就不能保证啊。博士应该是在某一方面有自己独到见解和研究的人，而现在的博士，由于数量太大，很多博士都在做类似的研究。")]),_._v(" "),v("p",[_._v("我在北欧讲学时，发现他们那里博士的数量很少，因为他们的博士必须要达到很高的程度才能毕业，中途达不到标准淘汰出去的很多。他们在国际上很著名的科学家，带七八个博士就觉得很多了，他们要保证用足够的资源和经费来把自己的博士培养得很充分。但我们有的博导要带几十个博士，我目前带的在读博士也有九个。你想，一个人再有精力也不可能带那么多的博士，何况硕士还有一大批。所以，我们国家博士的水平并不突出，而那些水平很差的博士并不利于我们顶尖人才在国际上的定位，也使得一些水平很高的博士常常在国际舞台上被淡化掉。")]),_._v(" "),v("p",[_._v("经济观察报：我们是否也可以把博士毕业的门槛提高？")]),_._v(" "),v("p",[_._v("翟婉明：这个在中国现阶段还做不到，毕竟中国人很难说撕破脸皮不让你毕业，所以，我们只能控制入口。这是很微妙的。")]),_._v(" "),v("p",[_._v("经济观察报：现在的学生跟你上学时比有什么区别？")]),_._v(" "),v("p",[_._v("翟婉明：过去的大学生跟现在的可不太一样。那时候好多人都是很不容易才从农村考上大学的，受教育的机会少，人单纯，学习也比较刻苦。现在的学生，在学习精神上没那么刻苦，但他们天资好，大脑开发得早，在小学、中学就得到了很好的训练，即使从营养方面讲，也比我们好。")]),_._v(" "),v("p",[_._v("经济观察报：你如何对待你带的学生？")]),_._v(" "),v("p",[_._v("翟婉明：我带的研究生、博士生大多数都是从农村出来的，肯学，能吃苦。我经常告诉他们，你们的任务就是好好学习，认真做研究，待遇什么的不用想，只要做得好，物质方面的东西就会比你们能想像到的还要好。")]),_._v(" "),v("p",[_._v("经济观察报：你对目前的科学界怎么看？")]),_._v(" "),v("p",[_._v("翟婉明：现在的科学界有着各种各样不同的声音，大加赞赏的有，尖锐批评的也有。搞科研工作有一个特点，就是要能安下心来，而现在发展太快，暴露出了一些问题，因为机会越来越多，弄得大家都挺浮躁。很多人先到处弄项目，然后弄点论文检索，弄些指标就对付交账。这种心态下弄出的项目有多大意义是可想而知的。")]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=t.exports}}]);