(window.webpackJsonp=window.webpackJsonp||[]).push([[810],{1170:function(t,r,e){"use strict";e.r(r);var _=e(14),n=Object(_.a)({},(function(){var t=this,r=t._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[r("p",[r("em",[t._v("引用")]),t._v(" "),r("a",{attrs:{href:"http://epei1008.blog.163.com/",target:"_blank",rel:"noopener noreferrer"}},[t._v("老顽童"),r("OutboundLink")],1),t._v(" 的\n"),r("a",{attrs:{href:"http://epei1008.blog.163.com/blog/static/235248602009325105454590",target:"_blank",rel:"noopener noreferrer"}},[t._v("唐山交大“土老五”同学录后的《补记》"),r("OutboundLink")],1)]),t._v(" "),r("p",[t._v("|  |  |")]),t._v(" "),r("blockquote",[r("p",[t._v("【老顽童按】原单位王副总工程师是唐院的前辈校友，现旅居美国。到南京后我问她女儿是否有他父亲大学时的资料和照片，结果她给了我她父亲的几本校友录和校友通讯。《唐山交大“土老五”（1946—1952）通讯录》是其中一本。“土老五”？为何如是自称？原来唐山交大土木工程系在五十年代前学制不像我们六十年代是五年制，而是四年制，1946年入学的学子本应是50年毕业，但遇上朝鲜战争，结果未及毕业就几乎全部参加了抗美援朝，回国后又再补毕业设计课程，结果这批毕业生是足足待了五年，比其他系同期毕业生多上一年，所以就被戏称为“土老五”了。")]),t._v(" "),r("p",[t._v("翻看这本具有特殊含义的同学录，除了王总外，我只认得马基琳，是我画法几何和制图课老师，也是把我招进唐院的领路人。翻到最后，看到了编后《补记》，顿时激起我对其人生感触的共鸣和同窗情的深深感动，于是将其贴上来供校友们阅读。不知您读后是否也会与我有同感？")])]),t._v(" "),r("hr"),t._v(" "),r("blockquote",[r("p",[t._v("**"),r("br"),t._v("\n唐山交大“土老五”（1946—1952）同学录"),r("br"),t._v("\n补 记"),r("br"),t._v("\n文/土老五 1990年**")]),t._v(" "),r("p",[t._v("同学录理应包括曾同过班的同学，无奈我们这趟列车中途上下车的过多，难以统计也无法寻觅，为避挂一漏万之嫌，乃将范围限于毕业时号称“土老五”的七十二位弟子。")]),t._v(" "),r("p",[t._v("提起弟子，更不能忘怀已故的罗忠忱老师、顾宜荪老师、武镜湖老师、黄寿恒老师、李斐英老师、范治纶老师和罗河老师等等。他们既给我们传授知识，更以一丝不苟、百折不饶的“唐山精神”塑造了我们。对日前去世而终身关注母校兴衰的茅以升老校长怀着虔诚的敬意。在汇集同学录资料的过程中，同学们纷纷向健在的杨耀乾老师的学识与品德表达了崇敬之情，并寄以健康长寿的祝福。")]),t._v(" "),r("p",[t._v("我们这些莘莘学子来自全国十八个省市，京都一别竟是四十年，这四十年在历史的长河中，固属弹指一挥间，但对我们来说，却经历了人生的黄金时期。我们进入角色之初，正值祖国发展变革的年代，满腔热忱胸怀报国之志，但生活不都是甜蜜的，有的一帆风顺，有了成绩；有的一生坎坷，壮志未酬；更多的默默奉献着，位卑而不敢忘忧国；无论遭遇如何，均以可贵的“唐山精神”编织着各自金色的梦。我们这一代对事业的追求堪称认真的、尽心尽力的。回首往事，无愧于祖国，无愧于师长，无愧于人民。"),r("br"),t._v("\n对先我们而去的六位学友，深表缅怀之情。")]),t._v(" "),r("p",[t._v("四十年后的今天，我们这些幸存者也相继退出角色，分散在十八个市镇。没有了面具，回复了自我，庸俗的市侩等级观念不再是我们真诚友情的障碍，让我们藉此同学录，彼此加强联系，勾起美好的回忆，或能使我们生活得年青些。"),r("br"),t._v("\n1996年母校 百年大庆，愿“土老五”诸公以古稀之年相会在成都。")]),t._v(" "),r("p",[t._v("1990年12月")])]),t._v(" "),r("hr")])}),[],!1,null,null,null);r.default=n.exports}}]);