(window.webpackJsonp=window.webpackJsonp||[]).push([[818],{1179:function(v,_,p){"use strict";p.r(_);var t=p(14),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("blockquote",[_("p",[_("strong",[v._v("1948年随校南迁记")])]),v._v(" "),_("p",[v._v("**"),_("br"),v._v(" "),_("strong",[v._v("文/严良田")])]),v._v(" "),_("p",[v._v("作者简介：严良田，我校交通运输学院教授。1947年考入唐山工学院土木工程系学习，毕业后留校任教，从事铁路车站与枢纽工程的教学与研究工作。")]),v._v(" "),_("p",[v._v("1948年11月下旬，我随“迁校委员会”组织的最后一批南下师生大队离开唐山，去天津候船。至1949年7月中旬，押运图书仪器从上海返回唐山，中经萍乡待命，前后共计8个月。这段迂回曲折的经历，在44年后回忆起来，心情还是不平静的。个人记忆力有限，记人记事恐有不尽准确之处，敬请读者指正。")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("我们还会再见面")])]),v._v(" "),_("p",[v._v("通过南迁派与反迁派的较量，迁校局面逐渐明朗。迁校委员会已经组织了“南迁先头部队”和第一批南下师生共约一百数十人，离开唐山。1948年11月18日，马如璋（马振）同学邀请反迁各路人马的代表，共同研究反迁形势。当场决定：反迁活动告一段落，个人去留自己作主，及早选择。")]),v._v(" "),_("p",[v._v("在此之前，建筑系何性天同学曾邀我一起学习宣传画，我们已经作了一些准备工作，但还未正式开始。这一天，他来找我，私下跟我交换意见。他说，他已决定不离开唐山，希望我也不要走。我考虑，我是靠奖学金维持生活的，不跟学校走，就切断了经济来源；同时，我还是想在学校里认认真真地学点东西。因此，我说，我已决定跟学校走，学校去哪里，我就跟到哪里。他表示理解我的心情。我们互相鼓励、相约，今后在不同的岗位上，继续进行斗争。我保证，在把学校迁回唐山的斗争中，竭尽全力。接着，他用自己的照相机给我留了影，这张珍贵的照片，至今我还珍藏着。")]),v._v(" "),_("p",[v._v("我们还会再见面！")]),v._v(" "),_("p",[_("strong",[v._v("下 船 斗 争")])]),v._v(" "),_("p",[v._v("等了几天，好容易有了船，大家都上了船。那时，平汉铁路、津浦铁路都已停运。水路畅通，只是缺船。有了船也不是客轮，而是美国送给国民党政府的登陆艇——万利号。临近开船时，刘正祥同学赶到码头。他本人已经决定不随大队南下，这次来码头是为了执行任务。我们见了面，他说明来意。大意是解放战场形势发展很快，平津唐一带很快就要解放了，希望学校能够留下来。退一步说，希望有一部分同学留下来，暂时到解放区去。上级准备派这部分同学随解放军进城，接管工矿企业。我把这个意思告诉了郑仲增同学，我们两个都同意留下来，不乘船南下了。我们去找迁校委员会，说明来意。迁校委员会某负责人，极尽刁难、推托、搪塞之能事。他说，你们下船可以，但行李已经进舱，不能翻动，取不出来。")]),v._v(" "),_("p",[v._v("最后一次反迁斗争，终因人数太少，没有形成一支力量，宣告失败。")]),v._v(" "),_("p",[_("strong",[v._v("万 利 轮")])]),v._v(" "),_("p",[v._v("船终于起锚了。缓缓地离开码头。岸上那些千方百计想搭船南逃的国民党军人，已经无法再来找麻烦了。这是，才有心想看看船，看看海。")]),v._v(" "),_("p",[v._v("万利轮制造工艺比较先进。当时，中国制造的船体，钢板与钢板之间是用铆钉铆接的。万利轮没有铆钉，全部是焊接的。看了这些焊缝，感到中国落后了，必须迎头赶上。")]),v._v(" "),_("p",[v._v("轮船驶出渤海湾，进入黄海以后，风浪渐渐大起来，能够感觉到船在颠簸。有些同学开始躺下休息。有些同学不思饮食，少数同学开始呕吐。据乘过船的同学介绍，进入黑水洋以后，风浪还要大，我们赶快从甲板上下到舱里。同船的唯一的女同学李秀芝，也跟男同学挤在一起，她没有受到应有的优待，感到对不起她")]),v._v(" "),_("p",[_("strong",[v._v("上海——杭州")])]),v._v(" "),_("p",[v._v("1948年12月6日，船到上海。二百多名师生员工，暂时在上海交大校园内安顿下来。有消息说，经校友努力，已借妥新建的萍乡中学校舍，学校准备在萍乡复课。")]),v._v(" "),_("p",[v._v("这时，辽沈战役已经结束，淮海战役激战正酣，黄伯韬兵团已被全歼，徐州已经解放，平津战役也已开始。12月12日，唐山解放。")]),v._v(" "),_("p",[v._v("据报，国民党唐山驻军在解放军强大的压力下，仓皇撤离。厂矿、铁路、桥梁、未遭破坏。这条消息可能有两种含义。其一，在党的领导下的工人护厂、护路、护桥组织，力量强大，敌人无从下手；其二，北平的掌权者为自己留了后路。报纸上的消息，有时是向政治家作出的某种暗示。但是，当时学校的掌权者，只热衷于迁、迁、迁。根本不愿探讨第二种含义存在的可能性。于是，12月15日，唐山交大专列，急匆匆驶上沪杭线。")]),v._v(" "),_("p",[_("strong",[v._v("杭州——萍乡")])]),v._v(" "),_("p",[v._v("12月16日，专列到达杭州。大队人马在杭州休息一天，据说是为了学生游览西湖提供方便。我站在杭州站外的一个空旷处所，向西湖方向远远地望了一眼，流落他乡的愁苦心情油然而生，不免长叹一声。何处是归程！")]),v._v(" "),_("p",[v._v("12月17日，专列改道浙赣线，首途萍乡。浙赣线是单线铁路。专列几乎站站停车，而且停车时间很长，行车速度很慢。每到一站，我们总要下车看看。在一个车站上，一列运送军队的列车出了事故，机车脱轨。几辆车翻倒在地，乘车的军人有伤亡。司机被军人架走。列车四周有荷枪实弹的军人站岗，如临大敌。")]),v._v(" "),_("p",[v._v("生活用水很难找到，专列上没有饮用水，有时一连几个站都找不到饮用水。不得已，只好拿着脸盆，去央求司机给点锅炉废水。困难是难以尽言的。")]),v._v(" "),_("p",[_("strong",[v._v("萍 乡 待 命")])]),v._v(" "),_("p",[v._v("12月21日，唐山交大专列到达萍乡车站。连续五天的旅途生活，学生们都已疲惫不堪。安排好食宿，就开始待命了。一些家在附近的学生，陆续离开萍乡，回到自己的家乡。学校不断地减员。尽管仍有部分学生在萍乡待命，不忍散去，但是，唐山交大昔日的高大的光辉形象，已经黯然失色。我常常暗自观察、分析过往行人的目光，看看他们是否把唐山交大认作“流亡大学”，把交大学生认作“流亡学生”。")]),v._v(" "),_("p",[v._v("萍乡没有学校的负责人，也没有迁校委员会的负责人，在萍乡复课的事再也无人提起。也就是在这时，常常回忆起离校前的那段斗争生活。迁校委员会中的那些超积极的委员们，他们现时的表现完全证实，当初他们极力主张迁校，并非为了唐山交大的前途。长达两个多月的萍乡待命，没有使学生们麻木不仁，他们变得老练了，要去投入新的战斗。")]),v._v(" "),_("p",[_("strong",[v._v("打回上海迎接解放")])]),v._v(" "),_("p",[v._v("学校前途无人问津，正是学校前途重放光明的前兆。")]),v._v(" "),_("p",[v._v("蒋介石引退，李宗仁表示，愿以毛泽东主席提出的八项条件为谈判基础。表面上，大环境松快了许多。1949年1月底，三大战役全部结束，两军隔岸对峙。2月初，孙科的行政院逃亡广州。学校的前途有望了。")]),v._v(" "),_("p",[v._v("这时，刘维俊、巩永龄、胡竟嵘等同学得到消息，学校准备在上海复课。消息激动人心。在此之前，也曾传出过学校继续前往台湾、广西等地的消息。以其不得人心而反应冷淡。因此，这次必须抓住时机，把去上海变成现实。“打回上海迎解放”这个口号，就是在这种形势下提出来的。这个口号颇得人心，鼓舞了士气。由萍乡回上海这关键的一步，几乎没有遇到什么阻力。")]),v._v(" "),_("p",[_("strong",[v._v("上 海 复 课")])]),v._v(" "),_("p",[v._v("大队人马兴冲冲地回到了上海。住进上海交大新文治堂。经过短暂的筹备，“五、三一”图书馆首先开馆，随后学校图书馆也部分开馆，摆出一幅复课的架势。这时，北平已经举行了解放军入城式，中共中央、解放军总部，毛泽东、朱德、刘少奇、周恩来、任弼时等主要领导人已经进入北京。陕北新华广播电台迁入北平，更名为北平新华广播电台。也已开始播音。实际上，正如大家提的口号，打回上海，是为了迎接解放！")]),v._v(" "),_("p",[v._v("这群唐山学生是敌是友，国民党特务们一时还摸不清底细。蒋介石的“和平姿态”必须加以利用，《短波》报应运而生。这份以传播北平新华广播电台记录新闻为内容的小报，及时报道了震惊中外的“渡江命令”和“解放南京”等重大新闻。党的外围组织延伸到唐山交大学生中间。“中国新民主主义青年联盟”开始在我校学生中发展盟员，准备在解放上海的斗争中护厂、护校，迎接解放。")]),v._v(" "),_("p",[_("strong",[v._v("“四、二六”大逮捕")])]),v._v(" "),_("p",[v._v("1949年4月23日，南京宣告解放，解放军逼近上海。上海国民党当局准备作垂死挣扎，加紧迫害进步学生。武装军警以铁甲车开道，深夜闯开上海交大后门，蜂拥而进。为首的，手里拿着黑名单和照片，企图抓捕学运领袖，一网打尽。新文治堂前架起机枪，严密封锁。这就是臭名昭著的上海“四、二六”大逮捕。")]),v._v(" "),_("p",[v._v("学运积极分子早有准备，大多数都已隐蔽。来不及隐蔽的，如李南生等，找到唐振绪院长家中。唐院长为了保护学生免遭迫害，不顾个人安危，毅然将他们藏在自己家中。现在回忆起这段往事，仍然使人十分激动。大家不会忘记，在最危险的时刻，唐院长和学生们站在一起！4月27日，上海国民党当局宣布，上海交大等15所院校，紧急疏散。唐院学生被迫于4月底撤离上海交大，暂时转移到启明新村。")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("启 明 新 村")])]),v._v(" "),_("p",[v._v("启明新村地处上海郊区，是一个刚刚建成的宿舍楼群，住户还没有搬进去。学生住进以后，情绪很快稳定下来。他们知道，国民党当局的寿命不会很长了。")]),v._v(" "),_("p",[v._v("五月中旬，解放军进至上海近郊，在启明新村，能够听到远处的炮声。5月下旬的一天，郑仲增私下和我商量，昆山一带已经解放，我们赶快到昆山去找解放军。谁知，我们的动作还是晚了一步。5月25日，廖鼎烈同学信步走出启明新村时，猛然看到全副武装的解放军战士，向市区进发。他急忙跑回宿舍，高喊着：解放军来了！我们解放了！启明新村顿时欢腾起来。")]),v._v(" "),_("p",[_("strong",[v._v("欢庆上海解放")])]),v._v(" "),_("p",[v._v("从5月位25日开始，学生们就坐不住了。有些学生商量如何搞宣传，欢迎解放军。有些学生跑到街上看看解放军究竟是什么样子。解放军战士抱着枪睡在马路人行道上的情景，都是我们亲眼目睹的。启明新村附近贴了欢迎解放军、庆祝上海解放的标语。这时，市区的战斗还在进行。5月27日，上海全市宣告解放。宣传工作由孙经曙同学负责。一天，他从市里领来任务，要求唐山交大选派两名美术人才，到市里去帮助工作。郑仲增和我被指定去执行这项任务。我们到指定地点报到，分配给我们的任务是临摹宣传画。临摹的是套色木刻画，画面较小，只有64开。要求我们把它放大到一张报纸那样大。所幸只是临摹，不必创作，难度不算大，我们连续去了几天，胜利完成任务。")]),v._v(" "),_("p",[_("strong",[v._v("盟员见面会")])]),v._v(" "),_("p",[v._v("一天通知我到上海交大一个教室里去开会。到会的有二十多人，都是熟悉的同学。有我们年级的，如罗天洲、张汝熊、郑仲增、梁家瑞、廖鼎烈等同学，也有高年级的，如王效通、陈国藩等同学。")]),v._v(" "),_("p",[v._v("会议开始，由阎涛、陈国藩讲话。大意是，在唐院建立党的外围组织“中国新民主主义青年联盟”，原分两支，现在解放了，两只合并。今天盟员通知见见面，今后在工作中，互相鼓励，互相支持，共同把庆祝上海解放的宣传工作做好。")]),v._v(" "),_("p",[v._v("我是由陈国藩、廖鼎烈两位同志介绍入盟的。当时，白色恐怖横行，盟组织处于秘密状态。盟员都是个别发展的，秘密进行。盟员之间互相保密，没有横向联系。我们这些人朝夕相处，本无秘密可言。但对于入盟一事，却都守口如瓶。")]),v._v(" "),_("p",[_("strong",[v._v("唐山，你好！")])]),v._v(" "),_("p",[v._v("解放了，学生们欢欣鼓舞。上海终非唐山交大的故乡，应该赶快回到唐山去！")]),v._v(" "),_("p",[v._v("6月，唐山工学院军代表顾稀同志，委派李泳、赵锐两同志来到上海，他们的任务是迎接在上海待命得到师生员工回到唐山去。赵锐同志是我的同乡，初中同学，老同学相见，不免叙叙旧清。不久，大队人马乘专列离开上海，回唐山去了。")]),v._v(" "),_("p",[v._v("图书和仪器，未能与人员同行，但应及早启运。我到善后小组报了名，参与图书仪器的装箱、装车和押运工作。木工张琴生锯木板、钉木箱。我打下手，有时自己也学着干。有一批三角小凳，就是我拆散、装箱、运回唐山的。黄逸峰同志那时在上海管铁路运输，在军运十分紧张的情况下，批准唐院图书仪器到麦根路车站装车启运。7月中旬，全部图书仪器运到唐山。至此，唐山工学院师生员工、图书全部胜利回到唐山。")]),v._v(" "),_("p",[v._v("天翻地覆慨而慷。唐山，你好！")]),v._v(" "),_("p",[v._v("资料来源：《校史资料选辑》第二辑（1992年10月）")]),v._v(" "),_("p",[v._v("。")])]),v._v(" "),_("hr")])}),[],!1,null,null,null);_.default=n.exports}}]);