(window.webpackJsonp=window.webpackJsonp||[]).push([[828],{1190:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|  |  |  |  |")]),_._v(" "),v("blockquote",[v("p",[v("img",{attrs:{src:"http://img.blog.163.com/photo/1QhMCmv0DikVJs7VvY0yWQ==/1453536779734980756.jpg",alt:""}})]),_._v(" "),v("p",[_._v("**杰出的民主革命家杨杏佛"),v("br"),_._v("\n**")]),_._v(" "),v("p",[_._v("杨杏佛（1893—1933），名铨，又名宏甫。1893 年 5 月 4\n日生于江西玉山。是一位极具才华的科学工作者，同时也是一位不可多得的管理人才。又是一位杰出的民主革命家。1911\n年，杨杏佛考入唐山路矿学堂预科，辛亥革命爆发后，他离校南下，以同盟会会员身份，投身革命。南京临时政府成立后，杨杏佛任总统府秘书处收发组长，被称为“青年才俊”。不久，袁世凯窃据总统职位，他不愿留任，经孙中山批准，由稽勋局派赴美国留学，先入康奈尔大学学机械工程，毕业后，又入哈佛大学商学院，专攻工商管理及经济学。留美期间，与胡适、任鸿隽、梅光迪一起被称为“四俊”。他与任鸿隽、赵元任等人发起建立我国第一个综合性的科学团体——中国科学社。")]),_._v(" "),v("p",[_._v("|")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_6-UIIuWtolePyvY8xhSdAw==_4803933427521104502.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_6-UIIuWtolePyvY8xhSdAw==_4803933427521104502.jpg",alt:""}})])]),_._v(" "),v("hr"),_._v(" "),v("p",[_._v("杨杏佛 1918 年 10 月回国后，在南京高等师范学校（后改名东南大学）任工科主任兼教授。1924 年，任孙中山秘书。1925 年，随孙中山北上。3\n月 12 日，孙中山病逝北京，他任总理葬事筹备处总干事。他决心继续高举孙中山的联俄、联共、扶助农工三大政策为核心的新三民主义大旗。1926 年 7\n月，北伐战争开始后，杨杏佛负责北伐军在上海的地下工作，逐日将有关情报电达北伐军。他还担任了国民党上海市党部执行委员、宣传部长，同共产党人恽代英、侯绍裘等一起，共同反击国民党右派的分裂活动。他积极支持上海工人第三次武装起义，并被选为起义胜利后成立的上海特别市临时政府委员。1937\n年 3 月 21 日，中国共产党在法国出版的《救国时报》发表《上海起义的中心人物》一文，称杨杏佛的工作“大有助于上海起义的伟业”。1927 年 4 月\n12 日、7 月15 日，蒋介石、汪精卫相继发动反革命政变。8 月，杨杏佛在大革命失败后的茫茫黑夜里，在《现代评论》杂志上发表《牺牲与堕落》一诗明志：")]),_._v(" "),v("p",[_._v("“人们，你苦黑暗么？")]),_._v(" "),v("p",[_._v("请你以身作烛。")]),_._v(" "),v("p",[_._v("用自己膏血换来的，")]),_._v(" "),v("p",[_._v("方是真正光明之福。”")]),_._v(" "),v("p",[_._v("在血雨腥风的白色恐怖下，杨杏佛同情中国共产党人，为中国革命做了不少有益的工作。")]),_._v(" "),v("p",[_._v("1927 年 10 月，当时全国最高学术教育行政机关——大学院（后改教育部）成立。蔡元培任院长，杨杏佛为副院长。1928 年 4\n月，蔡元培创办并主持我国第一个综合性的最高科学研究机关——中央研究院，蔡元培任院长，杨杏佛任总干事。")]),_._v(" "),v("p",[_._v("1931\n年，蒋介石对中央革命根据地发起反革命军事“围剿”。杨杏佛根据他平时搜集的资料和亲自到江西进行数星期的调查所得，写成考察报告《共产党在中国的状况》。这篇报告最早打破了国民党反动派严密的新闻封锁，比较客观地介绍了革命根据地和红军的状况。美国进步记者斯诺正是从报纸上看到这篇报告后，才激发了他亲赴革命根据地采访的强烈愿望。")]),_._v(" "),v("p",[_._v("1932\n年，他与宋庆龄、蔡元培等人发起和组织了中国民权保障同盟。宋庆龄任会长，蔡元培任副会长，杨杏佛任总干事。其宗旨在于营救被迫害的政治犯，争取人民的言论、出版、集会、结社等自由。")]),_._v(" "),v("p",[_._v("中国民权保障同盟建立于白色恐怖极其严重的 20 世纪 30\n年代，战斗在敌人统治的心腹地带，起到了在当时历史条件下其它的群众团体难以起到的巨大作用。它实际存在的时间虽然不到一年，但它产生的巨大、广泛的影响却是深远的。它揭露了国民党反动统治的黑暗和暴行，曾经营救在狱中的的廖承志、陈赓、罗登贤、丁玲、潘梓年等共产党人和许德珩等许多进步人士。邓小平赞誉中国民权保障同盟，“为革命事业作出了独特的重大贡献”。因此，该组织被国民党视为眼中钉，必须除之而后快。他们首先向杨杏佛下了毒手。")]),_._v(" "),v("p",[_._v("1933 年 6 月 28 日晨，杨杏佛被国民党特务杀害，年仅 40 岁。")]),_._v(" "),v("p",[_._v("噩耗传出，各界人士悲痛欲绝，纷纷声讨国民党反动派罪行。宋庆龄立即发表严正声明：“杨铨为同情自由所付出的代价反而使我们更坚决地斗争下去，再接再厉，直到我们达到我们应达到的目的。”蔡元培在中央研究院为杨杏佛举行的公祭会上泣不成声地说：“同人等当以先生之事业为事业，先生之精神为精神，使后辈青年学子有所遵循，所以慰先生者如此而已。”")]),_._v(" "),v("p",[_._v("6 月 20\n日下午，在万国殡仪馆举行杨杏佛的入殓仪式。仪式举行时大雨倾盆，电闪雷鸣。事前国民党特务扬言，要在这天暗杀民权保障同盟的其他成员。但是宋庆龄、蔡元培、鲁迅等200多人蔑视暴力的威胁，以大无畏的气概，冒雨参加了入殓仪式。鲁迅走出家门时不带钥匙，以示自己不怕牺牲的决心。送殓回来，鲁迅无法抑制悲愤心情，挥笔写下千古绝唱：《悼杨铨》：")]),_._v(" "),v("p",[_._v("“岂有豪情似旧时，")]),_._v(" "),v("p",[_._v("花开花落两由之。")]),_._v(" "),v("p",[_._v("何期泪洒江南雨，")]),_._v(" "),v("p",[_._v("又为斯民哭健儿。”")]),_._v(" "),v("p",[_._v("杨杏佛牺牲后，中华苏维埃共和国临时中央政府机关报《红色中华》第94期刊登了潘汉年撰写的社论，号召中国人民“加紧团结起来，开展革命的民族战争，以血腥的斗争来冲破国民党法西斯蒂的白色恐怖”。")]),_._v(" "),v("p",[_._v("1983 年，在杨杏佛殉难 50\n周年之际，上海各界在市政协召开纪念会。中共上海市委第一书记陈国栋在讲话中称赞扬杏佛“是我国杰出的民主革命家和进步知识分子的典范”。他号召人们努力学习杨杏佛伟大的爱国主义精神和为人民革命事业而献身的崇高品质。1987\n年，上海人民为杨杏佛重建了陵墓，并举行了新墓落成仪式。")])])])}),[],!1,null,null,null);v.default=n.exports}}]);