(window.webpackJsonp=window.webpackJsonp||[]).push([[843],{1204:function(v,_,n){"use strict";n.r(_);var p=n(14),r=Object(p.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("|")]),v._v(" "),_("p",[_("strong",[v._v("精彩日志评论集锦（一）"),_("br"),v._v("\n文/唐院春秋访客")])]),v._v(" "),_("p",[_("strong",[v._v("《历届毕业生合影》相册评论")])]),v._v(" "),_("p",[v._v("网易博友 湖南长沙校友 2009-03-28 15:28")]),v._v(" "),_("p",[v._v("寄同窗"),_("br"),v._v("\n北风呼啸，万里晴空，天高地远，心奔友朋。"),_("br"),v._v("\n忆七十载之陈迹，"),_("br"),v._v("\n念几千里之英容，"),_("br"),v._v("\n思盈虚之长短，"),_("br"),v._v("\n盼身心之康宁。"),_("br"),v._v("\n北居南宅，"),_("br"),v._v("\n何日相逢。"),_("br"),v._v("\n处江湖之远而怀故，"),_("br"),v._v("\n居京城之侧而梦行。")]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-30 17:47")]),v._v(" "),_("p",[v._v("忆唐院，峥嵘岁月稠！"),_("br"),v._v("\n梦唐山，故地何日再重游？"),_("br"),v._v("\n莫道桑榆晚，"),_("br"),v._v("\n莫叹白了头，"),_("br"),v._v("\n少小离唐去，"),_("br"),v._v("\n老大回乡走。"),_("br"),v._v("\n涅槃凤凰城，"),_("br"),v._v("\n父老翘首迎！！！")]),v._v(" "),_("p",[_("strong",[v._v("《1971年唐院千里大搬迁》日志评论")])]),v._v(" "),_("p",[v._v("茴香斋主 2009-02-28 13:51：")]),v._v(" "),_("p",[v._v("峨眉地处破偏僻农村，交通，信息闭塞，学术交流既感不便，生活供应又很困难，教师队伍难以稳定，严重影响教学质量。学校整体素质下降，对唐院伤害极深。海内外校友闻讯，深感痛心，纷纷向有关部门要求，改变这个不合理的校址。直到1984年，上级终于批准在成都建设总校，学校才逐步走上正轨。")]),v._v(" "),_("p",[v._v("网易博友 浙江宁波校友 2009-03-12 22:57 ：")]),v._v(" "),_("p",[v._v("国家宏观的决策给交大带来莫大的损失！"),_("br"),v._v("\n昔日的辉煌渐行渐远，真的替交大惋惜。")]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-16 10:14 ：")]),v._v(" "),_("p",[v._v("呜乎！往昔不堪忆，来者犹可追！橘生淮南则为橘，橘生淮北则为枳。百年交大辉煌在唐山，今日交大欲重现昨日辉煌也只能回唐山！就象犹太人在欧洲在美国永远复不了国一样！三国时代的刘皇叔匡复汉室的梦想尽管有诸葛亮孔明二世后灰飞烟灭吗？唐山家乡时刻等待游子的归来，再造唐院辉煌！！")]),v._v(" "),_("p",[v._v("网易博友 香港校友 2009-03-18 17:06 ：")]),v._v(" "),_("p",[v._v("將唐山鐵道學院的原有校區歸還給交通大學, 設立交通大學唐山研究院, 是全國各族人民的共同心願！")]),v._v(" "),_("p",[v._v("唐山校友山山 2009-03-18 17:49 ：")]),v._v(" "),_("p",[v._v("A dood idea!"),_("br"),v._v("\nI consent to your proposal !")]),v._v(" "),_("p",[v._v("唐院学子 2009-03-18 19:31 ：")]),v._v(" "),_("p",[v._v("是个好主意，支持。")]),v._v(" "),_("p",[v._v("网易博友 河北校友 2009-03-18 19:34 ：")]),v._v(" "),_("p",[v._v("的确很不错，我也很希望学校或唐山的领导千万要保住这个圣地！")]),v._v(" "),_("p",[_("strong",[v._v("《院系调整 元气大伤》日志评论")])]),v._v(" "),_("p",[v._v("茴香斋主 2009-03-01 08:41：")]),v._v(" "),_("p",[v._v("1952年，对高等院校进行的院系调整，是学习苏联，要求各工业部门都要有所属的大专院校，没有的就新成立起来。在这次调整中，唐山交大的多个系，科及其教师学生调出，精华调去一半，元气大伤。学校直属铁道部领导，改名为唐山铁道学院。事实上，许多大专院校直属各部门产生不少弊端。1983年，一些有识之士，提出了“将直属于各部门的高等院校交教育部办，以利综合平衡发展和培养人才”的建议。此后多年，学校归教育部领导，才有了全面的发展。")]),v._v(" "),_("p",[v._v("网易博友 四川成都 2009-03-17 21:10 ：")]),v._v(" "),_("p",[v._v("在全国众多院校中本网页首开先河，可喜可贺，属不易。哈哈，支持，鼎力而为。")]),v._v(" "),_("p",[_("strong",[v._v("《黄万里对修建三门峡大坝说“不”》日志评论")])]),v._v(" "),_("p",[v._v("茴香斋主 2009-02-24 04:10：")]),v._v(" "),_("p",[v._v("黄万里先生，他和他那一代的中国知识分精英一样，他们从西方学到了先进的科学技术，更学到了科学，理性的精神。他们懂得科学的真理，是独立于个人或集团利益之外的。因此，他们决不会为权势或偏见而放弃科学的真理。他们这样的品质，精神和风范，为后人所景仰。")]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-03 15:47 ：")]),v._v(" "),_("p",[v._v("巍巍万里，我之楷模！科学公敌——张光斗！！！")]),v._v(" "),_("p",[v._v("网易博友 2009-03-04 02:45 ：")]),v._v(" "),_("p",[v._v("张光斗“认错”----犹抱琵琶半遮面")]),v._v(" "),_("p",[v._v("网易博友 浙江宁波校友 2009-03-12 23:09 ：")]),v._v(" "),_("p",[v._v("早就听说黄老学长的事迹，他的身上闪耀着老唐院的光芒！强过现在学子数倍")]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-25 17:58 ：")]),v._v(" "),_("p",[v._v("黄万里先生，坚持真理的大师，捍卫科学的旗手，只可惜，政治环境的影响，很多事情至今无法使人真正了解事实的真相。"),_("br"),v._v("\n唐山交大优秀的学子。"),_("br"),v._v("\n如今的西南交大能够秉承先生的这种精神，秉承老交大的这种唐山精神，实现交大的复兴，走在科学的前沿是可以的。")]),v._v(" "),_("p",[_("strong",[v._v("《几张珍贵的照片》日志评论")])]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-18 16:39 ：")]),v._v(" "),_("p",[v._v("交大，唐山，血肉相连！")]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-25 17:10 ：")]),v._v(" "),_("p",[v._v("唐山正处于快速发展期，尽管国家的支持力度不够，但是靠唐山人自己的奋勇向前的精神，唐山取得了很大的成绩。"),_("br"),v._v("\n如今的西南交大，发展的并不是很令人欣慰。我希望现在的交大是一所有独立思考的学校,如果因为现实社会的原因而丢了自己的过去烙印，那么他也就丢了自己的今后，迷失了自我。"),_("br"),v._v("\n失去独立思考的学校，也就失去了活力。"),_("br"),v._v("\n老交大的校友邹良严先生说的“唐山精神”是交大的财富。"),_("br"),v._v("\n交大人应秉承了这种“唐山精神”继续前行，同祖国倡导的一样，实现伟大的复兴。")]),v._v(" "),_("p",[_("strong",[v._v("《今日西南交通大学》日志评论")])]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-26 11:52 ：")]),v._v(" "),_("p",[v._v("大学者 非大楼之谓也，乃有大师之谓也！—— 梅以琦")]),v._v(" "),_("p",[v._v("网易博友 河北唐山校友 2009-03-26 17:15 ：")]),v._v(" "),_("p",[v._v("除了被改动了的校徽以及犀浦新校区大门背面的“唐山路矿学堂”还有些历史的痕迹外，如今的西南交大一片现代景象。怎么也看不出这是一所百年名校。"),_("br"),v._v("\n看看全国其它几大名校的校园历史建筑，交大就已经显得黯然了。"),_("br"),v._v("\n如网友留言 ------大学者 非大楼也，乃有大师之谓也。"),_("br"),v._v("\n交大希望你早日找回自己。")]),v._v(" "),_("p",[_("strong",[v._v("《无题有感——石家庄一校友留言》日志评论")])]),v._v(" "),_("p",[v._v("网易博客 河北唐山校友 2009-03-24 15:34 ：")]),v._v(" "),_("p",[v._v("求学来到唐，中华好儿郎！"),_("br"),v._v("\n赶上大革命，此生有痛伤。"),_("br"),v._v("\n七二迁峨眉，正操瞎折腾。"),_("br"),v._v("\n离唐三十载，至今尚彷徨！"),_("br"),v._v("\n科学示范区，主席钦定唐。"),_("br"),v._v("\n交大欲振兴，必须回家乡！"),_("br"),v._v("\n偏居西南隅，丧钟正在响。"),_("br"),v._v("\n当年蒋中正，迁都峨眉上，"),_("br"),v._v("\n抗战胜利后，还都金陵旁。"),_("br"),v._v("\n稳定刚开始，内战枪声响！"),_("br"),v._v("\n三年拼杀后，饮恨国家殇！"),_("br"),v._v("\n唐院迁峨眉，备战又备荒，"),_("br"),v._v("\n老毛树敌多，开辟多战场，"),_("br"),v._v("\n百年唐交大，无辜遭迁殃！"),_("br"),v._v("\n海内外学子，理应负担当，"),_("br"),v._v("\n复校于唐山，永誌不能忘！！！")]),v._v(" "),_("p",[_("strong",[v._v("《潭柘钟声》日志评论")])]),v._v(" "),_("p",[v._v("老顽童 2009-03-08 13:12 ：")]),v._v(" "),_("p",[v._v("前辈好文彩！佩服前辈历史知识的丰富，北京皇城的来龙去脉朗朗在口。"),_("br"),v._v("\n学妹也是在《地质队员之歌》的感召下，报考的唐院桥隧系，而且是第一志愿。但不如前辈的知识丰富，开始我填写的是京院地质系，以为北京的学校肯定好，是老师看了后，告诉我：“如果你真的喜欢铁道学院，那绝对是唐院教学实力比京院强，而唐院又以土木桥隧系为最强。”这样我才明白而纠正过来。那年月，我们考学校，是奔学校的优劣而去而不像现在的孩子是奔城市的大小而去的。"),_("br"),v._v("\n……(潭柘寺中的学习生活）这段的描述把当时其他大学都没有的清苦大学生活，描绘得如此单纯美好、生气勃勃，当年学长们积极向上的人生态度也是值得现今学子们应该好好学习的。")]),v._v(" "),_("p",[_("strong",[v._v("《怀念郭可詹教授》日志评论")])]),v._v(" "),_("p",[v._v("茴香斋主 2009-03-19 13:30 ：")]),v._v(" "),_("p",[v._v("郭可詹教授，早年留学英国，获数学博士。在1949年7月军委铁道部决定：国立唐山工学院，国立北京铁道管理学院，华北交通学院合并组建为中国交通大学，下设唐山工学院，北京铁道管理学院，7月13日中国交通大学正式成立，茅以升任校长，唐振绪任唐山工学院院长，8月20日军委铁道部令唐山工学院筹建铁道科学研究所，唐振绪院长随即撰写了“新唐院近景”一文，发表在国内外刊物上，文中介绍新唐院的任务和发展远景，动员海外学成的具有高水平的我国专家学者回国服务。在不到一年的时间内，就从美国，英国，台湾，香港和国内各地，引进了不少知名的专家学者，来充实增强的教师队伍，和研究所的科学研究力量。郭可詹博士就是在祖国召唤下，从国外回国，来到唐院任数学教授。"),_("br"),v._v("\n我是1953年考入唐院桥隧系本科的，何其有幸，我们年级的《高等数学》是由郭可詹教授给我们讲授的。他对基本概念的深入浅出的阐述，对基本原理的逻辑性的推导，对基本方法的独到的解析，将我们这些视学习数学为为畏途的学子，逐步引领进神秘的数学世界，初步领略到它的神奇和美妙。后来，郭教授又为我们班讲授了《弹性力学>,我有幸，是我班该门课的课代表，多有请益，聆听教诲。"),_("br"),v._v("\n1957年夏“鸣放”期间，1957年秋“反右斗争”的风暴席卷全国，唐院也和全国一样，大张旗鼓地展开。我们毕业班，推迟两个月毕业，参加“反右派斗争”。而在此前的“鸣放”期间，学校师生集中意见最大的是学校迁校问题，新校址长期没定下来，影响学校的发展。有人提议，成立一个“建校委员会”，来促进建校问题的决。于是推选出教师和学生代表组成的“建校促进委员会”，并推举出十多人的代表团，到北京直接向铁道部领导，提交反映唐院师生对建校问题的书面意见，但并没有得到铁道部领导的肯定的答覆。“反右派斗争”一开展，通过一个多月的“大揭发，大批判，大斗争”一个个“右派分子”给“挖”出来了。“建校促进委员会”成员，无论教师学生，几乎无一幸免。郭可詹教授因被推举为“建校促进委员会”成员，亦罗织其中。像一场噩梦一样，昔日的老师，同学，一夜之间成了“十恶不赦的阶级敌人”，堕入万劫不复的境地，他（她）们何罪之有？"),_("br"),v._v("\n眼见我景仰的郭可詹教授，被侮辱，被丑化，只有无言的悲愤。"),_("br"),v._v("\n痛哉，吾师！惜哉，吾师！")]),v._v(" "),_("p",[_("strong",[v._v("《一段往事》日志评论")])]),v._v(" "),_("p",[v._v("老顽童 2008-12-05 20:15 ：")]),v._v(" "),_("p",[v._v("64年已在大学校园中大唱阶级斗争的高调了。在学生中开展阶级斗争是个纲，纲举目张；阶级斗争要天天讲，月月讲，年年讲的教育了。这种宣传教育的结果，使得学生中出现了利用阶级斗争这个纲来打击另一些非贫下中农出身的同学。……这让我想起了64年班级里的一届团支部改选……"),_("br"),v._v("\n我想，为什么六十年代入学的学生之间的同学情和母校情结远比不上他们的前辈五十年代以前入学的同学情和母校情结来得深厚？这是很重要的原因。这种教育和宣传以及当年的各种政策（招生政策、助学金政策、入团入党政策等等）的家庭出身倾斜使得学生在校期间就学会了人整人的形而上学的方法。也为日后文革时期的“反动血统论”盛行铺下了思想基础。")]),v._v(" "),_("p",[_("strong",[v._v("《论“尧茂书、长江第一漂及其他”》")])]),v._v(" "),_("p",[v._v("网易博友 四川成都校友 **** 2009-03-14 19:35 ：")]),v._v(" "),_("p",[v._v("看了东门听雪先生20多年前关于尧茂书的讨论文章，深为感动。尧茂书首漂长江遇难后，当时成为一件感动全国的事件。但是，的确有一些不同的看法，特别是在一些青年之中。这就涉及到如何看待人生价值问题。东门听雪先生的文章对这些青年是一次很好的教育。特别是在当前一切向“钱”看的社会里，更有现实意义。谢谢东门听雪先生。")]),v._v(" "),_("p",[_("strong",[v._v("《呼吁：抢救利用唐山交通大学旧址》日志评论")])]),v._v(" "),_("p",[v._v("网易博友 四川成都校友 3-28 16:02 ：")]),v._v(" "),_("p",[v._v("全力支持！鉴于原唐山交大、唐山铁道学院已于1972年由国家确定迁至四川峨眉，改名为西南交通大学，并于1984年确定，总校扩建成都，已经没有可能再迁回唐山。建议唐山市政府部门与西南交通大学协商，充分利用原校址，建立历史陈列馆、办研究院、建立分校或其他形式，把西南交大与他的故乡唐山市永久的联系起来，还是可行的。这样可以告慰抚育唐山交大数十年的家乡父老以及从唐山毕业的众多校友，也可以加强唐山市与西南交大的合作，优势互补，共创双赢的大好局面。")]),v._v(" "),_("p",[v._v("唐山校友水水 2009-03-28 19:53 ：")]),v._v(" "),_("p",[v._v("唐山与西南交大有着浓浓的血脉亲情.原址的修复有利于挖掘唐山的文化底蕴,有利于增强文化明城的内涵,有利于提高城市建设的档次.同时,也有利于西南交大寻根明史,有利于交大唐山共同合作，有利于凝聚海内外校友爱国、爱交大、爱唐山的热情。一事百利，何乐而不为之？"),_("br"),v._v("\n目前，唐山已成为全国重要的钢铁、能源、原材料工业基地，GDP排名全国19位，超过好多省会城市。正在着力打造的“七大主导产业链”、“四大主体功能区”以及唐山湾“四点一带”的产业发展，为学科发展创造了条件，尤其是30万吨曹妃甸深水大港、超10吨南堡大油田、我国第一个国产时速350公里高速铁路动车组生产线、新首钢精品钢材生产基地、德龙海洋装备制造基地等，涉及海洋化工学科、装备制造学科（动车组、造船业）、物流学科（空港物流、港口物流）、水文地质学科、土木工程学科、交通通讯学科、海洋海事学科等。这为建设一流的大学实验室、工程研究中心，提供了坚实的学科基地与科研空间。"),_("br"),v._v("\n真诚的希望西南交大校友与唐山多交流，共努力，力促原址的修复。 我们的交流平台《渤海网-社区-\n唐山论坛》club.huanbohainews.com.cn/thread-4674-1-1.html唐山水水")]),v._v(" "),_("p",[_("strong",[v._v("《工56级毕业合影天安门》相片评论")])]),v._v(" "),_("p",[v._v("网易博友 湖南长沙校友 3-29 14:20 ：")]),v._v(" "),_("p",[v._v("北居南宅，"),_("br"),v._v("\n何日相逢。"),_("br"),v._v("\n处江湖之远而怀故,"),_("br"),v._v("\n居京城之侧而梦行。")]),v._v(" "),_("p",[_("strong",[v._v("《毛泽东与反右派斗争》日志评论")])]),v._v(" "),_("p",[v._v("网易博友 四川成都 2009-03-20 09:17 ：")]),v._v(" "),_("p",[v._v("以前，我总想不明白，毛泽东本身也是一个知识分子，为什么总是和知识分子过不去。解放后的知识分子的思想改造运动、反右派斗争，直至文化大革命，打翻在地，在踏上一只脚，叫你永世不得翻身。“两个估计”虽说是“四人帮”搞的，但是毛泽东圈阅的呀。难道建设社会主义就不要知识分子了吗。粉碎“四人帮”后，邓小平提出尧尊重知识，尊重人才，仅从这一点，就可以看出邓小平同志的英明，伟大。")]),v._v(" "),_("p",[v._v("网易博友 上海校友 2009-03-20 09:49 ：")]),v._v(" "),_("p",[v._v("邓是在文革中受到重大打击，而文革中那些始终不受信任的高级知识分子却无愿跟着四人帮蠢蠢欲动者，我想这才使他清醒反思了吧，但他在五十年代也是佷左的，“同情右派就是右派”据说出自他的口，所以扩大化功劳也非他莫属呢！")]),v._v(" "),_("hr")])}),[],!1,null,null,null);_.default=r.exports}}]);