(window.webpackJsonp=window.webpackJsonp||[]).push([[85],{448:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[v("strong",[_._v("根在唐山—访西南交通大学校长徐飞")])]),_._v(" "),v("p",[v("strong",[_._v("稿源： 唐山劳动日报 编辑： 冯叶")])]),_._v(" "),v("p",[_._v("**"),v("br"),_._v("\n**"),v("img",{attrs:{src:"/pic/www.huanbohainews.com.cn_pic_0_11_40_41_11404157_965946.jpg",alt:""}}),_._v(" "),v("img",{attrs:{src:"/pic/www.huanbohainews.com.cn_pic_0_11_40_41_11404158_739626.jpg",alt:""}}),v("br"),_._v("\n图为西南交通大学校长徐飞。 图为徐飞为唐山送上诚挚祝福。")]),_._v(" "),v("p",[_._v("环渤海新闻网专稿(本报赴西南交大采访组)四月的蓉城，草木葱茏，春深似海。漫步在西南交通大学的校园里，唐山元素扑面而来：一泓碧波轻漾的镜湖，一块镌着茅以升校长“奋斗”名言的石刻，明诚堂、天佑斋这些由最早的唐山路矿学堂延用百年的名字，让我们这些由唐来蓉采访的记者既兴奋异常，又百感交集。一座城市，一所名校，血脉相承，心手相依。4月15日下午，记者在西南交大犀浦校区专访西南交通大学校长徐飞，听他讲述这所百年名校的“唐山情怀”。")]),_._v(" "),v("p",[_._v("徐飞1996年获得西南交通大学博士学位，2008年9月任上海交通大学党委副书记，2012年11月任上海交通大学副校长，2013年9月掌舵母校，担任西南交通大学第65任校长。")]),_._v(" "),v("p",[_._v("我们的专访在绘有唐山路矿学堂校门的大幅油画背景下进行。")]),_._v(" "),v("p",[_._v("记者：我们这两天在校园里采访，接触到很多人，老教授、老教工、校史研究人员，他们对唐山怀有很深厚的感情，西南交通大学辉煌于唐山，您对于唐院的这段历史怎么看？")]),_._v(" "),v("p",[_._v("徐飞：西南交大1896年建校，1905年迁址唐山，应该说在学校118年的历史当中，有66年是在唐山度过的，这段历史对学校的发展非常重要。这段时间虽然是“峥嵘岁月”，却是西南交大最辉煌的一个时期，给学校的发展奠定了非常坚实的基础，也赢得了学校在办学史上巨大的声誉。在66年当中从学校里走出了众多杰出的人才。据不完全统计，院士就有14名之多，包括茅以升、竺可桢、张维、严恺、刘恢先等等这些非常杰出的学者，不仅为西南交大赢得了荣誉，而且也为华人赢得了荣誉，对世界科学的贡献也非常大。另外还诞生了“两弹一星”的功勋人员，诞生了在工程勘测界的设计大师等等，所以西南交大的根基，她的魂都在唐山。")]),_._v(" "),v("p",[_._v("记者：在交大校园里，随处可见唐山元素，我们感受到学校很注意对唐山那段历史的传播和继承。")]),_._v(" "),v("p",[_._v("徐飞：的确如此。可能由于时间关系，你们看到的唐山元素还不是足够的充分，我想再补充几个你们尚未看到的唐山元素，比如说我们九里校区的南大门，上面非常明显的几个英文字母“TEC”，就是唐山交通大学的英文缩写，另外就是我们的眷诚斋，我们的明诚堂，我们镜湖宾馆里的多功能厅、院士墙等等一些老照片，包括我们犀浦校区的南大门，唐山路矿学堂这些标识，唐山元素都用得非常充分，更重要的是包括我们工作手册中的校徽、校歌和校训，都是一直延用当年唐山铁道学院、唐山交通大学的，迄今为止还激励着我们的师生员工包括广大的校友。")]),_._v(" "),v("p",[_._v("记者：唐山交大虽然已经于1972年搬到了峨眉，但她至今仍是唐山人心目中无法忘记和抹去的一个文化符号。西南交大近年在唐山创办了唐山研究院，现在唐山正在启动老交大遗址保护和复建工程，这让我们感觉到交大依然在我们身边，没有离我们远去。未来西南交大和唐山将会有怎样的合作？")]),_._v(" "),v("p",[_._v("徐飞：我觉得是唐山哺育了西南交大，唐山养育了西南交大，现在交大理应来反哺、来回馈唐山，这是天经地义的。我们全校师生员工对唐山那段岁月非常留恋，我本人自去年9月12日成为西南交大的第65任校长，来校第二个月，我就找时间去了唐山。说来非常惭愧，自己那么多年一直没有去唐山，担任西南交大校长以后，我是怀着非常崇敬的心情甚至是一种朝圣的心情去了唐山，这是个寻根的概念，去激活当年我们在唐山办学的那种艰苦岁月、治校的理念，去学习当年培养人才的精华的东西。党的十八大报告里，非常明确地提出企业是技术创新的主体，现在唐山发展的态势也非常好，我觉得高校在发展过程中，在产学研过程中，应该发挥更重要的作用。唐山的轨道客车有限公司以前在产学研方面做得非常好，前期的技术工作做得非常扎实，我想未来这方面的合作还要更加全方位。学校会充分发挥我们在科技、信息、能源、学科、平台乃至人脉方面的优势，希望跟唐车能够深度合作，在推进产学研，特别是在推进轨道交通发展中可以发挥更重要的作用。")]),_._v(" "),v("p",[_._v("记者：唐山媒体在前段时间也做了很多关于唐山交大包括西南交大的报道，向社会征集了很多的线索，很多唐山人对过去的唐山交大有很深厚的情感，如重建当年校址的呼声非常高，未来交大与唐山的合作，在教育方面有哪些具体的举措？")]),_._v(" "),v("p",[_._v("徐飞：唐山市委、市政府对学校非常支持，明确在旧址复建唐山的研究院，把唐山的研究院和旧址复建工程作为一体化的工程来推进，学校一直想在这片土地上能够更多地发挥学校的优势，所以想和市里合作，可能做一个国际教育园，把一些优质的国际教育资源和学校在学科方面、师资方面、教育手段、教学方面的一些优质的资源能够嫁接到唐山，把国际教育产业园办好。形式也非常多，一是利用唐山研究院方式来做，一是可能像西交利物浦、宁波诺丁汉这种模式办，把西南交通大学的优势、唐山市的优势，加上国外一些资源的优势，三位一体地有机嫁接在一起，推动唐山经济社会发展，在科技教育方面发挥我们自己的力量。")]),_._v(" "),v("p",[_._v("记者：我们在交大九里校区看到一个雕塑，三个人举着大拇指，中间是一个老人，两边是两个年轻人，现在每个大学都有这样的问题，有老中青这些教职工队伍，同时还要培育一代又一代的学生。我们知道交大最宝贵的就是交大精神，请谈谈如何把交大精神传承下去？")]),_._v(" "),v("p",[_._v("徐飞：交大精神非常重要的就是“双严”，严谨治学和严格要求，这是当年在唐山的时候奠定的基础，今天还拿这两句话来要求我们的学生，要求我们的员工。当下的学生的确非常优秀，他们有很多的优点，但我们觉得跟我们的上一辈，我们的前辈相比的话，研究方面改进和提升的空间非常巨大。今年是学校的“学风建设年”，我们要传承和光大在唐院所创造的这种双严的传统，能够把它秉持好，发扬好，在新的历史时期，在21世纪高等教育蓬勃发展的情况下，把“双严精神”真正变成我们骨子里面、血脉里面不断生长的一种精神的元素，成为我们未来发展中坚强的一种力量。")]),_._v(" "),v("p",[_._v("记者：您能给我们讲一讲交大精神的内涵吗?")]),_._v(" "),v("p",[_._v("徐飞：“竢实扬华、自强不息”对我们来讲是最贴切不过的两句话，西南交大从唐山到贵阳、再到四川，包括从峨眉到成都，从九里到犀浦，这一路走得筚路蓝缕、波澜壮阔，非常坎坷，但是一直坚持锲而不舍、坚韧不拔、自强不息的精神才有今天这样的辉煌和地位。我们一直强调责任就是一种担当，“竢实扬华”要求我们：一旦有能力有实力的时候，千万不要忘了自己要去扛一份责任，这个国家这个社会这个民族要有一份担当，这是我们最最重要的一种精神元素。")]),_._v(" "),v("p",[_._v("记者：本月19日，您要去唐山参加西南交大全国校友会长第三届联席会议，这次把会址选在唐山有什么初衷？")]),_._v(" "),v("p",[_._v("徐飞：为了整合广大的校友资源，加强校友和母校之间的联系，从2012年开始我们就做了一个决定，实行联席会制度，就是召开全国各地乃至世界各地西南交大的校友会长的联席会议。2012年我们在成都家门口开了首次联席会，2013年在上海，由西南交大上海校友会承办的。今年第三届联席会放在唐山来开，我们认为唐山是我们的根，所有的校友说到唐山，都有浓浓的一种情怀在里面。另外也是利用京津冀协同发展宝贵的机遇，共襄盛举、共谋发展。校友、母校、唐山人民，可以把各自的优势来整合来齐聚来放大来创造，所以第三次年度校友会会长联席会我们放到了唐山。这次我们的议题包括与唐山市委、市政府就唐山交大旧址复建的问题、交大唐山研究院的一体化的问题、如何利用产学研合作推动区域经济发展问题等等，总之不管是从产业的角度，从教育资源的角度，还有高端人才培养的角度来讲，把各自的优势充分发挥出来。")]),_._v(" "),v("p",[_._v("记者：如您刚才所说，西南交大贯穿了这种寻根情结，这是否也加深了交大的教职员工包括学生对唐山的了解，更加深化了唐山与交大之间的联动？")]),_._v(" "),v("p",[_._v("徐飞：我觉得唐山和西南交大、和成都的这种联络、这种联动是必然的，大家知道，整个世界经济都一体化了，京津冀、长三角、西三角的联合和联动是未来大势所趋。交大、唐山两个地方的优势都非常明显，怎么能做到优势互补、各展其长，在开放、合作、共享、多元的过程中创造新的辉煌，是唐山和西南交大要共同回答的一个问题。我相信在双方的共同努力下，一定会结出丰硕的成果。")]),_._v(" "),v("p",[_._v("采访结束后，徐飞校长欣然提笔，为西南交大的故乡唐山送上诚挚的祝福：创新驱动，转型发展，再创辉煌！")])])}),[],!1,null,null,null);v.default=n.exports}}]);