(window.webpackJsonp=window.webpackJsonp||[]).push([[869],{1230:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[v("strong",[_._v("不能忘却的纪念")]),_._v(" ："),v("br"),_._v("\n钱塘江大桥总工程师罗英"),v("br"),_._v("\n2015年3月30日  来源: 浙江工人日报  特约撰稿:陈慈林文/供图")]),_._v(" "),v("hr"),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img2.zjolcdn.com_pic_0_16_61_71_16617117_370964.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("罗英(左二)陪同当时的省府官员参观大桥")])]),_._v(" "),v("p",[_._v("今年4月1日是钱塘江大桥正式动工80周年纪念日。")]),_._v(" "),v("p",[_._v("钱塘江上建桥，是千秋伟业。这里，除了我们所熟知的科学家茅以升、力推建桥的时任浙江省建设厅厅长的曾养甫外，还有一位功臣不能忘却，他就是钱塘江大桥的总工程师罗英。")]),_._v(" "),v("p",[v("strong",[_._v("同窗同舟共济，技术报国")])]),_._v(" "),v("p",[_._v("1934年初，国民政府浙江省建设厅厅长曾养甫在浙赣铁路局长杜镇远的推荐下，聘请茅以升担任钱塘江桥工处处长，担纲修建钱塘江大桥。当时，曾养甫恳切地对茅以升说：“筹款任务由我承担，建桥重担由你负责，包括用人权。大家一起完成建桥使命，为中国人争气。”")]),_._v(" "),v("p",[_._v("茅以升日后回忆道：“桥工处开门第一件事是组成建桥班底……最要紧的是要请到一位可做主要助手并能代替我的工作的人。”")]),_._v(" "),v("p",[_._v("这位主要助手，茅以升选择了他在美国康奈尔大学桥梁专业的同学罗英。")]),_._v(" "),v("p",[_._v("罗英毕业后即回国在铁路工作，曾任山海关桥梁厂长。按惯例，钱塘江大桥总工程师应由桥工处长兼任，茅以升却请罗英担任此职，他说，“罗英是一位富有经验的桥梁工程师。有他来和我合作，共同指挥桥工的进行，我就觉得更胆壮了……”")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_wvBq_peCP6bb4YfzHThSiQ==_4540191374360077782.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("罗英（左七）和茅以升（左八）在开工典礼上")])]),_._v(" "),v("p",[_._v("当时摆在他们面前的困难很多：之前中国所有公路和铁路上的较大桥梁，均由外国人设计和承建。究其原因，一是国家积贫积弱，工业落后，在材料、机具供应和技术能力上都缺乏基础；二是当权者崇洋媚外，轻视本国技术人员和工人；三是建设经费大多从外国银行借贷，使用上受到挟制。")]),_._v(" "),v("p",[_._v("随后的建桥过程中，罗英面对种种困难，以知识报国，追求卓越，与茅以升同舟共济，为建桥发挥了不可替代的作用。")]),_._v(" "),v("p",[v("strong",[_._v("设计方案“暗度陈仓”")])]),_._v(" "),v("p",[_._v("当时几乎没人相信中国人能在钱塘江上建桥。为了减轻上层阻力和向外国银行顺利借款，曾养甫“明修栈道，暗度陈仓”：明的请铁道部顾问、美国桥梁专家华德尔设计方案，暗中却让茅以升与罗英加紧设计，提出自己的方案。")]),_._v(" "),v("p",[_._v("华德尔毕竟不了解钱塘江水文特点，他选的桥址江流湍急；设计方案中铁路、公路与人行道三桥并列，增加了修建难度和经费，其总预算需银元758万元。")]),_._v(" "),v("p",[_._v("而茅、罗方案是将桥址选在河床较稳定的闸口附近，并采用我国桥梁史上首创的公铁双层联合桥型：下层为单线铁路，上层为双线公路与人行道，总预算只需510万银元。当时中日战争一触即发，这个方案中还有一个亮点就是，16孔钢梁跨度都是67米，如某孔被炸毁，可用岸边那孔救急。")]),_._v(" "),v("p",[_._v("虽然茅、罗方案明显优于华德尔，但在向铁道部争取投资和外国银行借款时，曾养甫一直声称只是对华德尔方案“略予修改”。等到浙江省建设厅最终落实建桥费用时，才公开宣布钱塘江大桥“完全是我们国人自行设计的。”")]),_._v(" "),v("p",[_._v("事实证明了这个方案的科学性：抗战胜利修复大桥时，就是用北岸第一孔钢梁修复被炸毁的那孔梁；公铁双层联合桥型更是因结构合理、稳定性好，被解放后我国修建的武汉、南京和芜湖等长江大桥所广泛采用。")]),_._v(" "),v("p",[v("strong",[_._v("建桥艰难罗英殚精竭虑")])]),_._v(" "),v("p",[_._v("1935年4月1日，大桥正式动工，茅以升作为桥工处长，全面负责建桥工作；罗英作为总工程师，负责设计和指挥施工。在建桥过程中，罗英殚精竭虑，始终在一线处理各种实际问题。")]),_._v(" "),v("p",[_._v("![](/pic/img.bimg.126.net_photo_0wnbgZdjNEfQQ-\nMNdkbwAw==_4541317274266922708.jpg)")]),_._v(" "),v("p",[v("strong",[_._v("罗英（右三）陪同省公路局、水利厅官员参观打桩")])]),_._v(" "),v("p",[_._v("按照惯例，通常是先做完下部基础，次做桥墩，再架设钢梁。为缩短工期，罗英提出“立体”施工方式：不按顺序，哪个桥墩先完成基础，就接着做桥墩，桥墩完工后马上架设钢梁。这种方式提高了劳动力利用率，但增加了施工管理难度。罗英作为总工程师，白天在工地指挥施工，夜间伏案审阅图纸，还要思考次日工作中可能出现的问题，并预想解决困难的方案。")]),_._v(" "),v("p",[_._v("一天，罗英发现桥墩围堰钢板桩打歪了需返工，但钢板沉陷河床拔不出来。有人说算了吧，罗英说，质量问题不能马虎。他带着工友们把气锤倒装在钢板上，向上反击拔出钢板后重打。")]),_._v(" "),v("p",[_._v("![](/pic/img.bimg.126.net_photo_sgYlx8Zm-\nfM7jhT5E0dStQ==_3945716223547154053.jpg)")]),_._v(" "),v("p",[v("strong",[_._v("茅以升（左五）罗英（左三）视察一号墩打钢板桩")])]),_._v(" "),v("p",[_._v("位于江心的6号墩，正好建在江底石层斜坡上，坡面落差达8.5米。工人们在沉箱内作业：把高处凿低，低处用混凝土垫高。罗英与茅以升多次亲自进入沉箱指挥施工。一天，日寇飞机前来轰炸，要不是管理空压机的工友冒着生命危险坚守岗位，万一空压机停掉了，罗英与茅以升就会被倒灌的江水淹死。")]),_._v(" "),v("p",[_._v("在两年半建桥过程中，罗英协助茅以升克服各种困难，先后研究出“射水法”、“沉箱法”、“浮运法”等新技术，大大提高了工程进度。")]),_._v(" "),v("p",[_._v("1937年9月26日大桥终于通车。20多年后，茅以升在《钱塘江建桥回忆》一书中深情地表示：“我们两人历经风波，甘苦与共，成为桥梁事业的好伙伴。”")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_kjG0X44bZi6ftZFLKUiMIg==_5135510950103097560.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("茅以升（左三）与罗英（左一）陪同曾养甫 (左二)在桥上检查视察")])]),_._v(" "),v("p",[_._v("解放后，罗英潜心研究中国桥梁史，写了不少相关著作。")]),_._v(" "),v("p",[_._v("1964年7月1日，罗英病逝于上海，茅以升亲笔书写了这样一副挽联：")]),_._v(" "),v("p",[_._v("“ "),v("strong",[_._v("学桥、造桥、写桥，一旦间沉疴突变，遗著伤心余半部；")])]),_._v(" "),v("p",[v("strong",[_._v("良友、益友、诤友，五十年坠欢难入，前尘回首痛重泉")]),_._v(" ”")]),_._v(" "),v("p",[_._v("茅以升亲自主持了追悼会。")]),_._v(" "),v("p",[_._v("为纪念罗英对建桥的贡献，有关部门为罗英铸造了铜像，2013年11月1日，来自美国、台湾和北京、南昌、武汉等地的20多名罗英后人，参与在钱塘江大桥纪念馆安放罗英铜像，并捐赠罗英珍藏的建桥史料。")]),_._v(" "),v("p",[_._v("![](/pic/img.bimg.126.net_photo_-\nMKIEh03UQtQkuQS7wrlIg==_4566650022170861729.jpg)")]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);