(window.webpackJsonp=window.webpackJsonp||[]).push([[874],{1236:function(t,r,n){"use strict";n.r(r);var e=n(14),o=Object(e.a)({},(function(){var t=this,r=t._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[r("p",[r("em",[t._v("引用")]),t._v(" "),r("a",{attrs:{href:"http://epei1008.blog.163.com/",target:"_blank",rel:"noopener noreferrer"}},[t._v("老顽童"),r("OutboundLink")],1),t._v(" 的\n"),r("a",{attrs:{href:"http://epei1008.blog.163.com/blog/static/2352486020091252148258",target:"_blank",rel:"noopener noreferrer"}},[t._v("读[永久的墓碑]有感"),r("OutboundLink")],1)]),t._v(" "),r("p",[t._v("|  |  |")]),t._v(" "),r("p",[r("img",{attrs:{src:"/pic/img.bimg.126.net_photo_IeMji51GsT5r5q3VwyLntA==_425308689811505503.jpg",alt:""}})]),t._v(" "),r("p",[t._v("**读不老松2009.2.11日志[永久的墓碑]有感"),r("br"),t._v("\n文/老顽童 图/互联网"),r("br"),t._v("\n原文链接地址："),r("a",{attrs:{href:"http://hyl2808.blog.163.com/blog/static/5749204220091117455659/",target:"_blank",rel:"noopener noreferrer"}},[t._v("http://hyl2808.blog.163.com/blog/static/5749204220091117455659/\n"),r("OutboundLink")],1)]),t._v(" "),r("p",[t._v("**")]),t._v(" "),r("p",[t._v("不老松年近八旬，是我十分敬重的前辈，昨日去拜访她的博客，看到她2月11日转贴的一篇文章，题目是《永久的墓碑》，(作者：杨继绳，新华社高级记者，学者,《炎黄春秋》杂志社副社长。)\n内容主要阐述了59～61大饥荒年代的一些鲜为人知的情况。看后深有感触，对作者持有的历史观，也很赞同。"),r("br"),t._v("\n大饥荒时，我刚上初三，学校政治课老师说，现在阶级敌人很猖狂，到处造谣生事，蒋介石还叫嚣反攻大陆，同学们一定要提高警惕，回家做好宣传。那年月我和作者一样无限热爱和信任党、老师和学校。回家听大人在小声议论安徽逃难来的难民说安徽饿死很多人的事，我想起老师说的话，立即把母亲叫到房间里，对她说这是不可能的事，不要相信阶级敌人造谣，要相信党！以至于和母亲之间产生很不愉快的结果。"),r("br"),t._v("\n谁知文革前的65年上半年，我校大学生去四川参加农村四清，我的工作组组长是洪雅县政府管农村口的干部，晚上没事时，给我们摆龙门阵摆到大饥荒年代，说他到各个乡去了解灾情，晚上看不清，一走一绊的，不知摔了多少跤，爬起一看就是一具尸体，用手电一照，路边、桥上到处都是，……他叹息一声，那年月四川饿死不少人哪！我听得汗毛直竖，此时才幡然醒悟，天府之国尚且如此，安徽那是肯定饿死人更多了，看来是我错怪了母亲。"),r("br"),t._v("\n文革开始后，我听到了更多情况，据说安徽饿死人达300万，为此安徽省副省长桂××作为替罪羊被贬官至我校当个吃饭不管事的副院长。"),r("br"),t._v("\n我们这代人从小受的理想主义教育，单纯而盲目，谁说又不是愚民政治的牺牲品呢？那场文革触及了所有人的灵魂，不论你是身居高位的政客，还是底层蝇营狗苟的草民，谁都难以幸免，也使我们在痛苦的挣扎中深省，觉悟，反思。"),r("br"),t._v("\n我深为赞同作者的还历史以本来面目的观点。要把真实留给后代，不仅是大饥荒，还有大跃进运动，反右运动，反右倾运动，文革运动，……不该发生的已经发生了，一切都已成历史，但我们要勇于直面曾经的惨烈，曾经的愚昧和曾经的黑暗，不该回避，不能粉饰，只有这样才能汲取教训，演绎好未来的历史，必须让千秋万代牢记——\n藐视生命、文明倒退的历史不可重演！！！"),r("br"),t._v("\n联想到时下从中央到地方，以至学校、企事业单位在编撰党史、地方史志、企业史志、校史志乃至历史教材时，对建国后至改革开放前这段历史不是持回避、遮掩的态度，就是文过饰非，避重就轻，或不谈事实，没有数据，空洞无物的一番定式口号：“党和国家一贯认为……，始终坚持……”，就如戴上一付永远正确、一贯正确的面具，用今天的政策不能翻过去的案这等似是而非的论调来掩盖错误等等。但“认为”不等于“所为”，今天你对人民、对世界努力去负责任不等于你过去一直都做到负责任了。对历史的态度，也是一块试金石，可以试出我们是否真的信奉辩证唯物主义和历史唯物主义，诚然，有人会说历史从来就是由强者书写的，请问从历史的长河来看，哪个强者不是风云一时呢？文革时四人帮可谓强极一时，为所欲为篡改党史、军史、连历史照片都不放过，用弥天大谎来掩盖历史真相，国家主席也成阶下囚，但人心军心呢？尽失！不过转眼云烟矣。能纠正四人帮的错误，难道就不能正确面对自身的错误吗？只要今天改正了错误，检讨过去的错误不但不会失民心，反而会以真诚凝聚民心，何况文明发展到今天，高科技、卫星通讯、互联网的普及发展，许多事实真相，想盖也难。"),r("br"),t._v("\n我母校的校史问题，就是一个缩影，建国前的老唐院校史早就出版了，改革开放后的西南交大校史轻松编纂中，唯独从建国后至改革开放这段新唐院的校史，就像难产儿，难以催生，除了许多珍贵的资料毁于文革或搬迁中遗失，最大的心理障碍就是心存顾虑，有人就说许多历史运动，中央尚无定论，我们也就难以执笔，不如空白地翻过去。当贾老师跟我谈及此时，我的回答和《永久的墓碑》作者不谋而合地一致：先实事求是地写出来，是非曲直暂且不论不评，我们要做的就是对历史负责，对后代负责，将真实的史料留给后人，任由后世去评判，如再不抓紧去收集编写，那个年代的人都相继老去，真实的细节也会被带走。"),r("br"),t._v("\n我们遗憾，我们没能创造一个辉煌时期留给后人，但我们不能把虚假作为遗产留给后人，我们能做到的就是负责任地把真实和教训留给他们，让他们以史为镜，变教训为财富，少走弯路，能较为顺利地去创造未来的辉煌！"),r("br"),t._v("\n老顽童斗胆坦言了。")]),t._v(" "),r("hr"),t._v(" "),r("p",[r("strong",[t._v("附1：不老松 2009-2-11 日志全文（因封而略）")])]),t._v(" "),r("hr"),t._v(" "),r("p",[r("strong",[t._v("附2：互联网资料")]),r("br"),t._v(" "),r("img",{attrs:{src:"http://img.bimg.126.net/photo/9t5aPYTGoUnSGCqg1KHNLQ==/1986087435671767569.jpg(",alt:""}})]),t._v(" "),r("hr")])}),[],!1,null,null,null);r.default=o.exports}}]);