(window.webpackJsonp=window.webpackJsonp||[]).push([[877],{1238:function(t,s,n){"use strict";n.r(s);var r=n(14),v=Object(r.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("p",[t._v("|")]),t._v(" "),s("p",[s("strong",[t._v("我校朱炜同学")])]),t._v(" "),s("p",[s("strong",[t._v("喜获'中国大学生自强之星标兵'殊荣")])]),t._v(" "),s("p",[t._v("由共青团中央、全国学联主办，新东方教育科技集团协办、中国青年报承办的2008年度“中国大学生自强之星”评选活动，自2008年10月中旬启动以来，全国956所高校的9701名大学生参选。活动全程有447万人次通过网络实名注册，参与投票。经过校级、省级、全国评选和评委会评选，最终产生10名“中国大学生自强之星”标兵，100名“中国大学生自强之星”，800名“中国大学生自强之星”提名奖获得者，他们将共同分享由新东方教育科技集团提供的220万元“中国大学生新东方自强奖学金”。最终，我校公共管理学院2007级硕士研究生朱炜喜获2008年度“中国大学生自强之星标兵”殊荣和“中国大学生新东方自强奖学金”特等奖。")]),t._v(" "),s("p",[t._v("朱炜，男，汉族，中共党员，公共管理学院2007级硕士研究生。出生于我国最大的彝族聚集区--\n四川省凉山彝族自治州会东县的一个偏远闭塞和极端贫苦的深山农村，经历过青春叛逆、苦难屈辱和成长蜕变，但他从来没放弃过自立自强追求卓越的价值和信念。在本科期间，他创建了大学生创业联盟，通过创业基本解决学费和生活费，未向学校申请任何资助和贷款等。研究生阶段，他组织“研究生志愿者团队”全力投入到四川5.12地震救灾重建和志愿服务中，并在学习科研、创业实践和社会公益活动等多方面表现突出。2008年获得团成都市委颁发的“抗震救灾志愿服务荣誉证书”、我校第五届“大学生自强之星”、研究生综合一等奖学金、“竢实扬华奖章”等多项荣誉。朱炜同学自立自强和热心社会公益的价值和信念是当代大学生最为可贵的精神财富。")]),t._v(" "),s("p",[t._v("共青团中央和全国学联举办此次评选活动旨在希望在广大青年学生中发现和树立一批勤奋学习、不畏困难、勇于进取的先进典型，引导更多的学生向他们学习，努力成长为中国特色社会主义的合格建设者和可靠接班人。2008年度“中国大学生自强之星”颁奖会于2009年1月18日下午在北京举行。团中央书记处书记卢雍政、中共北京市委常委梁伟、全国学联主席刘凯、团中央学校部部长李小豹等领导出席颁奖会并为10名标兵颁奖。会上，卢雍政书记强调，希望广大青年学生以“中国大学生自强之星标兵”为榜样，勇于面对当前暂时的困难，加强理论学习和实践锻炼，时刻保持昂扬向上的精神状态，在战胜困难的过程中实现个人的成长进步。")]),t._v(" "),s("h2",{attrs:{id:""}},[s("a",{staticClass:"header-anchor",attrs:{href:"#"}},[t._v("#")]),t._v(" **")])])}),[],!1,null,null,null);s.default=v.exports}}]);