(window.webpackJsonp=window.webpackJsonp||[]).push([[9],{374:function(_,t,p){"use strict";p.r(t);var i=p(14),v=Object(i.a)({},(function(){var _=this,t=_._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[t("hr"),_._v(" "),t("p",[t("strong",[_._v("校友厅——只有一层的“校友楼”")]),_._v(" ****")]),_._v(" "),t("p",[t("strong",[_._v("作者：梁锦唐")])]),_._v(" "),t("p",[_._v("在老唐院的校园中，有一个称之为“校友楼”的建筑。校史记载：这个建筑是时任唐山工学院院长的孙鸿哲和教授们提议，校友们捐资，由当时任助教的李汶设计。1935年5月15日校庆日，校友楼正式举行奠基仪式，原设计图纸是两层，因资金不足，只修了一层，即投入使用。解放后，有了基建费用，但因校址下面储有煤层即将开采，开滦煤矿通知我校，为安全起见，校内建筑慎建楼房，因此原设计蓝图始终没有实现。")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVavqhpD13yLCTOAovenFnDohiaqiaEzAmibnkDgPdMHicVAibpgerjtkkqKeg_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[t("strong",[_._v("图片引自春源视界公号之《西南、北京交大校徽研究》")])]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVacrUm437fEQTGO1BE9gdJTsRGBGA5Sictw3eicg6fGic8peOJLiaJMicZibJg_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[t("strong",[_._v("图片说明：从这个角度拍的校友厅可以隐约看到校友厅西边的北华1舍，这栋建筑当时是与校友厅配套的客房。")])]),_._v(" "),t("p",[_._v("因为这个校友楼只有一层，后人称之为“校友厅”，但老人们还是习惯上叫它“校友楼”。\n这个建筑物的设计理念很超前，其房屋的修建和室内装修是由校友创办的荣华工程公司和上海清华工程公司承建。校友楼竣工才一年多，“七七事变”爆发，美丽的校园落入日寇之手。据史料记载，从1937年7月17日日军占领唐山交大，到1945年8月15日日本投降，日本第二十七师团、日本唐山宪兵队、陆军宪兵队、日本独立第八混成旅、日本唐山驻军联络部、日本华北特别警备队先后有数万人在交大驻扎，伪冀东政府也设在了交大校园内，期间校友楼成为日军将校集会所。")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVaibwgq6AJ2r9K7j9H7sZj6xXdLqmQORUcXRveRxyGicbFh1wvXWMI636Q_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[t("strong",[_._v("图片选自《慷慨悲歌冀东魂》一书，原照片是从日本军队的相册中发现的。")])]),_._v(" "),t("p",[t("strong",[_._v("从照片背景的窗户可以看出，照片是在校友厅门前的大阳台上拍的。")])]),_._v(" "),t("p",[_._v("抗日战争胜利后，1946年8月23日在异地办学的唐院师生才回到阔别九年的唐山校园。1947年5月15日，学校举行了隆重的校庆纪念活动（当时建校时间以在唐山复校的1905年计算），期间出版了《国立唐山工学院四十二周年纪念特刊》（以下简称《纪念特刊》），在《纪念特刊》中一篇题目为“校友厅纪实”的文章，简述了校友厅的修建过程。并对其周边的景物进行了描述：“房屋形式中西兼具，新颖美丽，内部设备亦雅洁完整。前后院落杂莳花木鑿池植荷，或建亭山，每当春夏之交，水声潺潺树色苍翠如滴，群鸟飞鸣其间，嘤嘤相答颇饶林壑之胜……”")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVadIicCHPtrrs3MOtq6f2maTL8cPchGiaTuJGSJtbM9eMeHTsic6Z1rf9bw_640_wx_fmt=jpeg.jpg",alt:""}}),_._v(" "),t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVaFrL97adSSjGPAE107rNOotBeRK2ibMWyv2geSmJJsnlzI3iaGnhYDx6g_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[_._v("校友楼的设计很有特色，可谓中西兼备，门前是一非常大的阳台。解放初期，阳台上有很多陶瓷烧的圆凳，圆凳中间是空心的，所以搬起来不重，但不足之处是容易打碎。后来这些圆凳逐渐不见了踪影，估计全被损坏又得不到补充，从此，这个大阳台上就再也没有坐的地方了。")]),_._v(" "),t("p",[_._v("校友楼一进门是一个会议厅，一般的会议都可以在这里开，大约可以坐三四十人。据“校友厅纪实”文中所述，解放前，大厅内悬挂着孙揆百（鸿哲）院长遗像，“以示崇念贤哲之意”。大厅的右边是一个小会议厅，里面全是沙发，类似个会客厅。上个世纪60年代，里面装备了与铁道部开电话会议的通讯设施，还有留声机、收音机、电视机等设备，这在当时已经是很现代化了。")]),_._v(" "),t("p",[_._v("会议厅左边是个活动室，里面安放着一个台球桌，上个世纪50年代，台球并不是一个普及的运动项目，很多人都不懂这项活动的规则。不知道这个台球桌是什么时间添置的，估计主要是建国初期为校友楼里住着的苏联专家而设置的。因为在校友厅竣工时，所定制的室内贵重家具在战争期间都遗失殆尽，据《纪念特刊》介绍，抗战胜利后，除了在日军占领期间，被伪冀东政府拿走部分外，其余的家具，包括一些教职工私人的家具均被国民党天津日军军用品接受委员会马振通等官员强行拉到天津，不知下落，其数量竟有数十列火车。所以校友厅中的家具，应该都是1946年以后陆续添置的。")]),_._v(" "),t("p",[_._v("会议厅、会客厅、活动室的隔墙是由镶着玻璃的木门组成。当举行大型活动时，将门打开，三个房间打通，大厅可容纳百余人。会议厅北面有一个游艺室，这个游艺室的设计别具匠心，它的天花板由玻璃组成，所以采光非常好。尽管周边墙上没有窗户，但室内明亮，白天不需用灯光辅助照明。游艺室内靠北面墙有一个小水池，池内有上水石，上水石内埋有水管，打开水龙头，一股水柱从上水石中喷涌而出，洒落在水池内。游艺室内有一个兵乓球台，在打乒乓球时，还伴有潺潺流水声，让人仿佛置身大自然之中，这种感觉在体育馆里找不到。面向游艺厅左右各是走廊，左边（西边）进去是通向二楼的台阶，因二楼未修，可直通房顶了。右边（东面）走廊走到底向右拐通向客房，向左拐是校友楼的后门，出了后门又是一个阳台。校友楼里有几间客房，记不清了，只记得有和客房配套的厨房，卫生间和理发室。在上个世纪60年代落实知识分子政策时，理发室只接待副教授以上职称的教师，理发师是学校一名姓邢的师傅，他是学校专业理发师，手艺最好。学校面向师生的理发店，开在学校商业区（那时称之为“合作社”）旁的新华斋。而教授们到校友楼去理发就可以减少排队的时间，但费用似乎高些，即表现了对高级知识分子的照顾，又体现了公平的原则，好像那个时代人们对这种做法都能给予理解。")]),_._v(" "),t("p",[_._v("解放前，对校友厅的使用，学校制订了详细的规则，在《纪念特刊》中刊出，名称为“国立唐山工学院校友楼整理规则”（此文又将“校友厅”称为“校友楼”）。学校专门成立了“校友楼管理委员会”由林炳贤教授担任干事。规则中规定了“校外校友来校可免费在校友厅居住一星期”。校友厅是校友捐资修建，校友返校在此下榻还要交钱是有些说不过去。规则对校友楼中的客室、会议室、浴室、餐室、游艺室及厨房的使用办法都做出了规定。")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVaEnCUur5BXias7ugO4uibhiaSWQrfngzP9fjPMc3UhdPibsv3SvRSh6w2qQ_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[_._v("校友楼小会议室的东边又有一个用玻璃完全封死的暖房，里面的木架上放满了花盆。因为北方冬季较长，花期较短，学校用这个暖房培育鲜花，以便在公共场合随时能用鲜花烘托出喜庆的气氛。学校在校友楼的后面还专门建有更大的苗圃和暖房，花匠有魏师傅、崔师傅、戴师傅，他们都是终生在学校从事这项工作，直至退休。")]),_._v(" "),t("p",[_._v("房间内全部采用木板包墙，隔墙用的木门全是实木，玻璃都是不透明的花玻璃，有人回忆木门上刻有英文，在我的记忆中没有这个印象，但在台球厅的墙上设计了保险柜，这一点我印象很深。记得有一次在台球厅玩时，我打开了保险柜（那时，保险柜既不上锁，也没有密码），曾看到里面放着越南民主共和国授予钱应麟院长的“友谊”奖章和证书（钱应麟院长在学校任职期间，因为家在北京没有搬到唐山，一直住在校友楼）。")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVaiadExsbVkaxwU8viad8zGh9eRHk3SuHQ7sSb0gtBFr0DljQI5OMEOib1Q_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[_._v("校友楼周围修建了不到一人高的石头围墙，围墙大门直对校友楼门口，向南开，没有院门。院子的门口是条路，路旁靠院墙有两间平房，这两间平房是红砖砌成，好像不是当年修建的。解放后曾分别做过供销社，消防队，派出所，记得小时在那里买过副食品，里边还放过一辆消防车（是用人工压水方式灭火）。")]),_._v(" "),t("p",[_._v("校友楼院子里设计了一个水池，有一座石桥跨过水池，石桥园拱上有一个石雕的龙头，张开嘴的口中喷出水柱，流入池中。池中种了荷花，水池中间有一个石头灯柱，晚上灯光映在池面，让游人流连忘返。石桥上方修建了藤架，挂满了藤萝。院内的路灯造型是天坛，这种艺术造型的路灯在当时也是极少见。")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVa5O7ECwIH62iaaJoS7KicW1EpmTjdcN4OIDTCfYIjd7tUib0yGEMkfEUGg_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[_._v("孙鸿哲院长还请李汶老师设计了一个校徽，镶嵌于校友楼正门上方。这个标志深得大家喜爱，成为了我校的校徽。李老师设计的标志，外形为盾形，与美国土木工程学会（ASCE）的会徽和康奈尔大学校徽外形相同，其正中是T形图案，既代表工科学生常用的丁字尺，也是唐山工程学院英语的第一个字母，代表院名。中有篆文“交大”二字，代表交通大学，右上方有唐山二字，基下有三条横条，代表当时的三色校旗，右下方是一个经纬仪，左上方是一个铁锤，分别代表我校的土木和矿冶二系。左下方是两棵青松，寓意学校万年常青，为国家培育人才。")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVa4tzzqibzUPBQa379hjwGM5N5myia9EKAtB8K1wVByGuy9bCf1kQLkSicw_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[t("strong",[_._v("图片引自春源视界公号之《西南、北京交大校徽研究》")])]),_._v(" "),t("p",[_._v("解放后，校友楼成为接待来宾和召开一些小型会议的场所。后来，学校来了苏联专家，他们都住在校友楼。在上个世纪50年代，校友楼还接待过越南驻华大使黄文欢，朝鲜副首相李周渊等外宾。校友楼西边还有一栋洋房，北华一舍，也是接待来宾的客房，这栋洋房其建筑风格与1905年修建的教授楼北华二舍，北华三舍一样，估计也是当年修建的，只不过是只有一层。在校友楼，逢年过节也举行团拜、聚餐和小型晚会。很多在唐山交大工作和学习过的校友，都在回忆文章中描述过校友厅给他们留下的美好印象。罗河教授的儿子罗冀生在一篇回忆文章中写道：“校友厅建筑风格中西具备，极具匠心。周围陪衬小庭院，前院有荷花池藤架，池里有许多小鱼游来游去，小桥上有喷泉，周边全是绿茵草地。后院有苗圃花房。北院墙里有几棵高大的核桃树，每年秋天有不少核桃成熟掉下地来。我经常在这时去捡那些落在地上的核桃，剥去核桃皮，砸开核桃壳，核桃仁非常好吃。”他还写道：“1955年10月，苏联专家来唐院帮助教学工作，住在校友厅。学校在校友厅举行欢迎苏联专家宴会。听我父亲讲，苏联专家看到板栗，很好奇，吃了一粒，赞不绝口，感觉味道太美了。问板栗长在什么地方？他们从未见过！大家都哈哈大笑。”")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/mpt.135editor.com_mmbiz_jpg_cgdcv5MCXUZZ3JsXRQP1KJhC4ZtC1rVaicialA8rPsRGUo7eGicQUPWyUe8ORib4icKIMb0gcia0cNbOdS6yJ7RDeMibA_640_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),t("p",[_._v("1946年入学的校友沈熙明在《梦萦所牵老唐院》文中写道：“每年总有几次在校友厅内举行古典音乐唱片欣赏会，由喜爱音乐的年轻教师讲解作品，然后用一台落地电唱机连续播放，直至一曲终了。平时下午四点以后，校园内不时飘出古典音乐的几个乐章，在草地上，松树下听着优美的旋律，在精神上得到高雅的艺术享受。”")]),_._v(" "),t("p",[_._v("学校特地在校友楼安排了最好的中西厨师其中有个胖胖的方师傅，为中外专家和来宾服务，一位工友李师傅专门负责校友楼的日常管理，他除了回家吃饭以外，不管白天、晚上都在校友楼里，为在里面活动和居住的来宾们服务，一直工作到退休。由此，我想到学校光辉的历史不仅仅是由大师级的教授们书写的，很多终生服务于这所学校的教学管理人员、工人，在他们兢兢业业的努力下，才共同铸就了学校过去的辉煌。")]),_._v(" "),t("p",[_._v("校友楼（校友厅）其优美的建筑和周边的景致，也成为人们拍照的首选地点，几乎在学校学习和工作过的学生、教职工及家属都有在校友楼前合影的照片，现在人们只能凭这些照片唤醒对校友楼的记忆了。人们说只有失去的东西才觉得宝贵，在地震中被彻底破坏的校园建筑里，校友楼是我梦中经常出现一个场景。其实，凡是去过校友楼的人，里边的建筑风格和设施都会给其留下深刻的印象。地震后，原唐山市委书记杨远已升任河北省副省长，他在会见我校何志先副校长时曾特别询问，西南交大能否出资将校友楼重建，以便让在唐山交大工作和学习过的校友们在回唐山寻根时，能找回点当年的记忆。由此可以看出校友楼给杨远书记留下的印象也是极其深刻的。")]),_._v(" "),t("p",[_._v("参考资料：")]),_._v(" "),t("p",[_._v("1、《慷慨悲歌冀东魂》主编，郑文忠 中国联合文化出版社")]),_._v(" "),t("p",[_._v("2、《国立唐山工学院四十二周年纪念特刊》")]),_._v(" "),t("hr")])}),[],!1,null,null,null);t.default=v.exports}}]);