(window.webpackJsonp=window.webpackJsonp||[]).push([[921],{1281:function(_,v,t){"use strict";t.r(v);var r=t(14),n=Object(r.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("blockquote",[v("p",[v("img",{attrs:{src:"http://upload.wikimedia.org/wikipedia/commons/5/5e/LiuShaoqi.jpg",alt:""}}),_._v(" "),v("img",{attrs:{src:"/pic/photocdn.sohu.com_20070427_Img249739929.jpg",alt:""}})]),_._v(" "),v("p",[_._v("刘少奇委员长、周恩来总理来校视察")]),_._v(" "),v("p",[v("strong",[_._v("供稿/贾志良")])]),_._v(" "),v("hr"),_._v(" "),v("p",[_._v("1958年9月2日，这是一个令唐院人难以忘怀的日子。中共中央副主席、全国人大常委会委员长刘少奇，中共中央副主席、国务院总理周恩来来到了我们这所已经有着60年辉煌业绩的唐山铁道学院。下午4点10分，刘少奇副主席和周恩来总理在中共河北省委第一书记林铁和唐山地委第一书记刘汉生、唐山市委第一书记杨远的陪同下，微笑着走下被欢迎的人潮簇拥得再无法前进的汽车，神采奕奕地健步走向迎候在校门口的师生员工。看着刘副主席和周总理洗得发白的灰色中山装，看着两位开国元勋慈祥、和蔼的面容，看着他们微笑着频频向人群致意，所有在场的师生员工沸腾了，对领导的敬畏和拘谨都被激动和爱戴之情淹没了。大家拼命地往前挤，都希望自己能离领袖更近些，能仔细端详他们慈祥的面容，亲耳聆听他们谆谆的教诲，向两位敬爱的领导尽诉衷肠、深表情意。少奇副主席和恩来总理不断地向群众挥手致意，不停地与挤到眼前的师生们握手，亲切地与身旁的群众交谈。就这样，两位领导在人群形成的缝隙中缓缓前行，校园里充满了欢声笑语。")]),_._v(" "),v("p",[_._v("运输系58－1班的工农学生韩庭会，抑制不住自己激动的心情，走上前去，双手紧紧地握住领导的手，一时语噎，最后终于激动地自报家门：“我是工农速中升到大学读书的学生，今年我们工农学生来唐院学习的很多。”幸福、自豪的心情溢于言表。两位领导人听后，微笑着连声说：“很好！很好！”")]),_._v(" "),v("p",[_._v("当人们簇拥着领导来到假山附近时，欢迎的人群越聚越多，几乎无法再往前走了。见此情景，院党委副书记刘毅同志高声建议大家坐下，总理也和蔼地说：“来，坐下，大家一起坐下，我们也坐下。”于是和师生们席地而坐。"),v("br"),_._v("\n刘少奇副主席坐下后，问坐在旁边的桥隧系55－1班的励鉴泉同学：“念什么系的？学校有几个系？”"),v("br"),_._v("\n“学校共有6个系，我在桥梁隧道系学习。”励鉴泉认真地做了回答。少奇副主席听完，高兴地同她握了握手。"),v("br"),_._v("\n此时，周总理也正在同运输系的韩惠珍老师谈话。"),v("br"),_._v("\n总理问道：“你是哪个系的？”"),v("br"),_._v("\n“运输系的。”"),v("br"),_._v("\n“在运输系做什么工作？”")]),_._v(" "),v("p",[_._v("“助教。”韩惠珍刚答完，周总理又开玩笑似的问身边一个小孩：“你是做什么的？”正巧刘副主席紧接着韩惠珍的回答问了一句“助教？”巧合把大家逗得哈哈大笑起来。只见被问的小孩红着脸腼腆地说：“我是小学一年级的学生。”总理会意地笑着说：“啊，你是家属呀！”边说边和小孩亲切地握了握手。")]),_._v(" "),v("p",[_._v("有人搬来了两个凳子，两位领导没有就座，反而站在凳子上向大家挥手致意。场上又一次响起了热烈的掌声和“中国共产党万岁”“毛主席万岁”的口号声。两位领导人深深为群众的激情所打动，总理动情地说：“我能在这里呆一个月就好了。”")]),_._v(" "),v("p",[_._v("刘副主席和周总理从凳子上下来后，又在人们的簇拥下向校园内走去。在南讲堂前的大路灯处，他们再次停下来与师生们交谈起来。这时有人大声说道：“请少奇同志给我们讲话！”刘副主席和蔼地说：“时间来不及了，还是听你们说吧。”林铁同志在旁说：“欠下这笔债，下次再来还吧。”两句“解围”的话把大家逗得又笑了起来。当看到学生中有戴着垒球手套的运动员时，刘副主席主动问：“你手上拿的是什么？”机械系56－1班的穆佩蕙同学边递过手套边说：“打垒球的手套，准备参加比赛。”刘副主席接过手套绕有兴趣地试戴起来。周总理这时也问大家怎么没把垒球带来，桥隧系56－3班的李继同学说：“急着来看总理，忘记带来了。”总理询问了垒球的样子，最后又指着李继的脑门问“会不会把脑袋打坏”，再一次把大家逗得哈哈大笑。")]),_._v(" "),v("p",[_._v("周总理后来又问铁道系55－2班的郭殿杰同学是哪级哪班的，当得知在搞勤工俭学，研究试验陶土轨枕时，总理关切地问：“搞得怎么样？是世界先进水平吗？”由于项目还在试验中，郭殿杰不好意思地回答道：“还好。”")]),_._v(" "),v("p",[_._v("这时，两位领导已经来到图书馆前，离校友赠送的纪念灯柱不远的地方，图书馆对面的南讲堂里正有学校的勤工俭学成果展览会。院团委王润霖书记接着勤工俭学的话题，邀请总理参观展览会。由于时间关系，总理只是详细地询问了唐院勤工俭学的突出成果之一通古线、唐承线和滦青线的勘测设计情况。当得知我们是动员全校师生做的正规铁路干线，全长达好几百公里时，总理满意地点头赞许。这时又有人提议让总理讲话，总理也只和蔼地说：“今天不讲了，还是听听你们的意见。”")]),_._v(" "),v("p",[_._v("时间一分一秒地过去，将近半个小时的会见就要结束了。师生员工恋恋不舍地送两位领导步出校门。校园里再次响起了“中国共产党万岁”“毛主席万岁”的阵阵口号声。两位领导边走边挥手笑着说："),v("br"),_._v("\n“祝同志们身体健康！”"),v("br"),_._v("\n返回的路上，刘副主席特意向一位年近古稀的老人挥手致意。当他听院团委王润霖书记介绍说这是老教授邵福旿时，趋步向前，亲切地同邵教授握手，向老教授问好。"),v("br"),_._v("\n当人们怀着留恋的心情将领导们送出校门时，院党委刘毅副书记诚挚地邀请两位领导人来唐时再到学校来。周总理风趣地说：“这要预约。”")]),_._v(" "),v("p",[_._v("短暂的会见在一片笑的海洋中结束了。领袖向送行的师生挥手告别，师生们用充满激情的最为热烈的掌声欢送他们崇敬的领袖。汽车驶出了校门，人们仍然眺望着汽车远去的方向，久久不肯离去……")]),_._v(" "),v("p",[_._v("这是一次难忘的会见。短短的三十分钟，让交大的三千多名师生备受鼓舞。大家为党和国家领导人朴素的衣着，和蔼、近人的举止深深感动，都沉浸在兴奋和幸福的回忆中，许多人都将这次难得的会见写进了自己的日记，以作永念。"),v("br"),_._v("\n1958年9月2日，唐院历史上光荣的一天，它将永远地被记入学校的史册。")])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);