(window.webpackJsonp=window.webpackJsonp||[]).push([[925],{1287:function(n,v,_){"use strict";_.r(v);var r=_(14),b=Object(r.a)({},(function(){var n=this,v=n._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[v("hr"),n._v(" "),v("p",[n._v("|  |  |  |  |")]),n._v(" "),v("p",[n._v("《唐山地震四十年史料专辑》征稿启事"),v("br"),n._v("\n唐山市政协文史资料委员会")]),n._v(" "),v("p",[n._v("老师,您好!"),v("br"),n._v("\n今年是唐山抗震救灾40周年，为进一步弘扬唐山抗震精神，让唐山人民永远铭记全国军民在唐山抗震救灾中的英雄事迹和奉献精神，唐山市政协文史资料委员会拟编辑出版《唐山地震四十年史料专辑》，特向社会各界人士征集稿件。"),v("br"),n._v("\n望您将当年参加唐山抗震救灾中的人和事写出来，以更好地丰富和发展抗震精神内涵，铭记历史，鼓舞当代，激励后世。"),v("br"),n._v("\n现将唐山市政协文史资料委员会《唐山地震四十年史料专辑》征稿启事发给您。"),v("br"),n._v("\n诚邀您在百忙之中不吝赐稿。谢谢！")]),n._v(" "),v("p",[n._v("唐山市政协文史资料委员会"),v("br"),n._v("\n《唐山地震四十年史料专辑》征稿启事"),v("br"),n._v("\n2016年是唐山地震40周年，为了更好地保护唐山地震史料、提高史料的研究价值和社会利用价值，唐山市政协文史资料委员会拟出版《唐山地震四十年史料专辑》，特向社会各界人士征集稿件。"),v("br"),n._v("\n一、内 容"),v("br"),n._v("\n征集内容主要包括："),v("br"),n._v("\n1. 震前唐山（震前唐山城市发展史）"),v("br"),n._v("\n2. 地震灾难（地震科普、灾难发生）"),v("br"),n._v("\n3．抢险救灾（抢险救灾、自救互救）"),v("br"),n._v("\n4. 医疗救护（上海、山东等各地驻唐医疗队）"),v("br"),n._v("\n5. 伤员转运（转运全国各地的唐山地震伤员）"),v("br"),n._v("\n6. 卫生防疫（震后清尸、防疫）"),v("br"),n._v("\n7. 恢复生产、生活"),v("br"),n._v("\n8．重建家园（从简易房到正式楼房的搬迁过程）"),v("br"),n._v("\n9. 震后40年发展成就"),v("br"),n._v("\n10.其他（有关地震纪念活动等）"),v("br"),n._v("\n二、要 求"),v("br"),n._v("\n稿件要具有真实性、“三亲”（亲见、亲历、亲闻）性，要做到准确、具体，不贬损、不溢美，不夸大、不缩小、不杜撰。除文字资料外，还包括相关照片、画面、图表和影像资料的征集。所有稿件必须原创，不得抄袭、套改。稿件发表，即付稿酬和样书。"),v("br"),n._v("\n三、时 间"),v("br"),n._v("\n征稿截止日期为2016年4月30日。 四、方 式"),v("br"),n._v("\n纸质文稿和电子版文稿均可。"),v("br"),n._v("\n纸质文稿邮寄地址：河北省唐山市路北区西山道9号。收信人：唐山市政协文史委 刘健泽。"),v("br"),n._v("\n电子版发稿邮箱：3076490886@qq.com 。"),v("br"),n._v("\n联系人： 刘健泽 电话：0315-2801652"),v("br"),n._v("\n闫永增 电话：18633115379")]),n._v(" "),v("p",[n._v("唐山市政协文史资料委员会"),v("br"),n._v("\n2015年12月28日")]),n._v(" "),v("table",[v("thead",[v("tr",[v("th")])]),n._v(" "),v("tbody")])])}),[],!1,null,null,null);v.default=b.exports}}]);