(window.webpackJsonp=window.webpackJsonp||[]).push([[93],{453:function(n,v,r){"use strict";r.r(v);var _=r(14),b=Object(_.a)({},(function(){var n=this,v=n._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[v("hr"),n._v(" "),v("p",[n._v("转自唐山市城市规划局网站："),v("a",{attrs:{href:"http://www.tsghj.gov.cn/008/037/2014/03-07/2307,37.aspx",target:"_blank",rel:"noopener noreferrer"}},[n._v("http://www.tsghj.gov.cn/008/037/2014/03-07/2307,37.aspx"),v("OutboundLink")],1)]),n._v(" "),v("p",[v("strong",[n._v("西南交通大学唐山研究院概念规划设计征集文件")]),v("br"),n._v("\n**-------------------------------------------------------------------------------------------------------------------------------------------")]),n._v(" "),v("p",[n._v("西南交通大学唐山研究院概念规划设计方案征集公告"),v("br"),n._v("\n起 始 日 期：2014年3月11日"),v("br"),n._v("\n截 止 日 期：2014年3月25日"),v("br"),n._v("\n所 在 地 区：河北省唐山市"),v("br"),n._v("\n招 标 内 容："),v("br"),n._v("\n项目名称：西南交通大学唐山研究院概念规划设计"),v("br"),n._v("\n项目地点：唐山市"),v("br"),n._v("\n征集单位：唐山市城乡规划局"),v("br"),n._v("\n一、项目概况"),v("br"),n._v("\n项目选址：位于唐山中心城区南部，南湖郊野公园的北部，建设路与唐胥路交叉口的东北侧，西侧是植物园，北临美食广场，南侧与地震遗址公园相接。该区域是南湖郊野公园中交通最便利的地段。"),v("br"),n._v("\n项目规模：总体用地面积200余亩，分阶段建设。主要功能为人才培养与培训（按1000人研究生学生规模考虑）、产学研项目与企业孵化、纪念唐山交通大学辉煌历史。"),v("br"),n._v("\n第一期主要复建唐山交大核心建筑，建筑面积约20000㎡ 。用于承载研究院运行培训项目和产学研合作项目。同时也使之成为纪念唐山交大辉煌历史之所。"),v("br"),n._v("\n第二期项目主要用于产学研合作项目与国际学院起步建设，建筑面积考虑40000㎡。"),v("br"),n._v("\n二、项目背景"),v("br"),n._v("\n（1）市民热切关注唐山交大原址的建设，呼吁对其进行抢救性利用。"),v("br"),n._v("\n这些热心市民大都与唐山交大有着千丝万缕的关系，对唐山交大有着深厚的感情，希望政府能够恢复交大部分建筑及功能，以此留住对唐山交大的记忆，了却众多交大人的殷殷之情。"),v("br"),n._v("\n（2）2016年世界园艺博览会的承办，需要加强会址周边的服务功能。"),v("br"),n._v("\n唐山要呈现给大家一个精彩的世园会，不仅是世园会工作本身，还要有完善、系统的服务设施来保障世园会的顺利举办。")]),n._v(" "),v("p",[n._v("三、设计方式"),v("br"),n._v("\n将入选设计单位通过对场地的实地踏勘，将对场地资源作出评估，结合相似案例比较，对本项目规划范围内进行总体城市设计，明确功能布局以及平面布置，确定相关经济技术指标，同时进行概念性设计。(入选几家设计单位，尽快敲定)")]),n._v(" "),v("p",[n._v("四、报名条件"),v("br"),n._v("\n征集人须为合法注册的法人实体或由合法注册的法人实体组成的设计团队。"),v("br"),n._v("\n（1）征集人应具有国内规划建筑设计甲级资质。"),v("br"),n._v("\n（2）征集人应具有与此项目相关的设计经验，并具有同等规模项目设计经验，且业绩和信誉良好。"),v("br"),n._v("\n（3）本次征集接受联合体形式。")]),n._v(" "),v("p",[n._v("五、报名时需提供的资料"),v("br"),n._v("\n1、 设计单位简介。"),v("br"),n._v("\n2、 企业法人营业执照副本和资质证书复印件。"),v("br"),n._v("\n3、 法人代表授权委托书及委托人身份证。"),v("br"),n._v("\n4、 项目负责人及主要设计人员简介。"),v("br"),n._v("\n5、 设计单位及项目负责人近三年中设计的同类项目业绩简介。"),v("br"),n._v("\n6、 联系人及联系电话。"),v("br"),n._v("\n7、 填好公告后附表，并将此表单独发送至tssghjbyzx@sina.com。")]),n._v(" "),v("p",[n._v("九、报名时间及方式"),v("br"),n._v("\n1、 公告发布之日起至2014年3月26日下午16：00前为有效报名时间。"),v("br"),n._v("\n2、 投标可通过信函（时间以发件地邮戳为准）或电子邮件形式报名。")]),n._v(" "),v("p",[n._v("十、资格预审"),v("br"),n._v("\n1、 报名截止后，由唐山市城乡规划局组织相关部门及专家对投标人的情况、设计能力和工作业绩进行综合审查评比，选取投标\n人参与本项目设计，并发出正式邀请函及招标文件，所有设计要求将以招标文件为准。招标结果将以公告形式公布于唐山市城乡规划局网站上（/）。"),v("br"),n._v("\n2、 取得邀请函及招标文件后，入选投标人应于7日内，将参与项目设计决定以书面形式通知唐山市城乡规划局。")]),n._v(" "),v("p",[n._v("十一、联系方式"),v("br"),n._v("\n联系单位：唐山市城乡规划局"),v("br"),n._v("\n单位地址：河北省唐山市路北区北新西道8号"),v("br"),n._v("\n邮 编：063000"),v("br"),n._v("\n联 系 人：王伟英"),v("br"),n._v("\n联系电话：0315-6327551"),v("br"),n._v("\n传 真：0315-6327550"),v("br"),n._v("\nE—mail：tssghjbyzx@sina.com"),v("br"),n._v("\n二零一四年三月十一日")])])}),[],!1,null,null,null);v.default=b.exports}}]);