(window.webpackJsonp=window.webpackJsonp||[]).push([[930],{1290:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("|  |  |  |  |")]),_._v(" "),v("p",[_._v("公务局长赵祖康如何当上国民党最后一任上海市长")]),_._v(" "),v("p",[_._v("转自中国共产党新闻网（来源辽宁日报） 作者：陈冠任")]),_._v(" "),v("p",[v("a",{attrs:{href:"http://dangshi.people.com.cn/n/2013/0707/c85037-22107403-2.html",target:"_blank",rel:"noopener noreferrer"}},[v("img",{attrs:{src:"/pic/www.people.com.cn_mediafile_pic_20130707_70_12928886924328196614.jpg",alt:""}}),v("OutboundLink")],1)]),_._v(" "),v("p",[_._v("赵祖康（右二）随陈毅（右）视察上海防汛工程。")]),_._v(" "),v("p",[_._v("在解放上海的隆隆炮声中，有一个人还在国民党上海市政府“值班”。")]),_._v(" "),v("p",[_._v("他就是代理市长赵祖康。 《31个省、自治区和直辖市组成实录》详细记载了他当上海市长的经过。")]),_._v(" "),v("p",[_._v("赵祖康是国民党第九任上海市长。 1949年 4月，南京一解放，蒋家王朝的官员们个个都没心思在上海“待”下去，打定主意要逃。\n4月14日，上海市市长吴国桢以“遭染痼疾”为借口，坚辞上海市长之职。蒋介石没有办法，只好令市政府秘书长陈良代理市长。可是陈良也不想为蒋家王朝站最后一班岗，在被任命代理上海市长之时就开始考虑下一个代理市长的人选。")]),_._v(" "),v("p",[_._v("就在他百思不得其人时，他打开地下党设立在郊区、对准上海城区广播的陕北人民广播电台，突然听到电台里中共方面点名要上海老资格的经济专家颜惠庆、工务局长赵祖康以及张元济留下来，为建设新上海贡献自己的专长。陈良的眉头舒展开来：“好啊，共产党为我找了一个代理市长！”这个人就是工务局长赵祖康！")]),_._v(" "),v("p",[_._v("赵祖康是著名的公路桥梁专家。上世纪30年代毕业于美国康奈尔大学，专攻道路工程，抱着“交通救国”、“工程救国”的理想回国。可惜连年战乱，他先在国民政府的交通部任职，后又被派往上海担当技术官僚。他虽然官至上海市工务局长，但英雄无用武之地，越来越心灰意冷。\n1949年2月初，中共地下党派一位“李小姐”与他接洽，要求他能够留下来，共建人民的新上海。“李小姐”还给他送来了毛泽东的《论联合政府》、《目前的形势和我们的任务》等书。他把毛泽东的书包上《论美军登陆》的书皮，认真研读，一遍又一遍，心中开始亮堂起来，知道中国的光明前途在哪儿。之后，他以研究建桥情况为名，从工务局中拿出《市郊大桥地址图》交给地下党。而共产党的电台点名要他留下来共建上海，是上海地下党市委的建议，经华东局和陈毅报党中央和毛泽东同意的。")]),_._v(" "),v("p",[_._v("5月23日，就在解放军进攻上海市区的当晚，陈良找来赵祖康，说：“我找你来，是请你来代理上海市长！ ”")]),_._v(" "),v("p",[_._v("还没等赵祖康表态，他又接着说：“赵老兄，这事关国民政府的最后形象问题，国民政府前面在撤出南京时，只顾跑，撒手不管市政，秩序太坏，市民遭受到了极大的损失，在国际上造成了很坏的影响。行政院长何应钦给我来信，要求上海市政府维持到最后一刻，不能自行作鸟兽散。国军与共军打仗归打仗，国民政府还是政府嘛，不能自我解散，要像个政府坚持到最后一刻。”")]),_._v(" "),v("p",[_._v("这一 “临行交代”，赵祖康没有任何心理准备，他没想到自己搞了十几年的 “工程救国”一直没机会，现在国民党到了最后关头，竟然有人\n“慧眼识珠”，要他担任代理市长！他心中一阵突跳，想答应，可转念一想，是不是陈良试探自己呢？他正在考虑时，陈良跺着脚说：“老兄，你还犹豫啥，我再不走，飞机就走了啊!\n”")]),_._v(" "),v("p",[_._v("赵祖康还是面露为难之色，说：“你不如去找老资格的颜惠庆他们来代理。 ”")]),_._v(" "),v("p",[_._v("陈良连忙摆手，非常诚恳地说：“这些人都找过了，关键时候啊，谁都不愿出来挑担子！再说他们看到共产党早吓破胆了，哪还有能耐与共产党办交接？赵老兄，这代理市长就只有你最合适！\n”")]),_._v(" "),v("p",[_._v("说着，他扳着手指，一条一条说：“一，你在市政府任局长时间长，资格老，出任此职，大家接受。二，也是最关键的，你是技术官员，没有政治派系背景，共产党对你印象不错，不会为难你的。三，你在国民党官员中人品形象口碑最好。这些年来，你一直在政府交通管理要害部门负责，管的全是大工程，实权在握，只要略施小计，玩一点手脚，暴富就绰绰有余。然而，你没有这么做；老百姓也不会指斥你!这市长，只有你了，赵老兄！\n”")]),_._v(" "),v("p",[_._v("“好吧！ ”赵祖康只好答应了。陈良当天就离开了上海。")]),_._v(" "),v("p",[_._v("第二天，赵祖康赶到外滩市政府大厦。这时苏州河以南市区已解放，市政府大厦上的白旗在风中飘着，大门口没了往日的哨兵站岗。刚进门，秘书就敲门报告，说：“中共将在下午派人来商谈政权交接事宜。\n”")]),_._v(" "),v("p",[_._v("“哦，好的。 ”")]),_._v(" "),v("p",[_._v("赵祖康随即又给市政府各部门作了要求。")]),_._v(" "),v("p",[_._v("下午，他匆匆赶到与中共约定的见面地点——上海市经济委员会委员李思浩家中，与中共代表商谈交接事宜。双方想法比较一致，很快达成了共识。")]),_._v(" "),v("p",[_._v("5月26日清晨，赵祖康到公用事业部门现场办公。在他的督查下，上海市内的公用交通基本恢复正常，水、电、电话都没有中断。在力争各个机构正常运转的紧张工作中，赵祖康还参与了策反国民党守军的工作。苏州河北岸的邮电大楼上的青年军居高“死守”，冷枪冷炮阻碍解放军攻过四川路桥进入苏州河北岸。有一位解放军找到赵祖康，要求他进行劝降，赵祖康二话没说，让邮政局长向守敌喊话，下午，守敌摇出了白旗。")]),_._v(" "),v("p",[_._v("下午3点，解放军进入市政府，全面接管警卫工作，赵祖康站在大门口迎接他们，陪同警卫队负责人视察机关各局处，特别查看了档案室。然后，商谈了具体的交接方案。")]),_._v(" "),v("p",[_._v("按照双方的约定，28日，举行接管国民党市政府的仪式。")]),_._v(" "),v("table",[v("thead",[v("tr",[v("th")])]),_._v(" "),v("tbody")])])}),[],!1,null,null,null);v.default=n.exports}}]);