(window.webpackJsonp=window.webpackJsonp||[]).push([[931],{1300:function(_,v,t){"use strict";t.r(v);var r=t(14),p=Object(r.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("入则勤俭礼和 出则忠信廉诚"),v("br"),_._v("\n作者：本报记者 许琦敏 阅读次数：1324")]),_._v(" "),v("p",[_._v("赵祖康从先辈遗训中凝练出八字家训传予子孙")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/ggw.tongji.edu.cn_themes_59_userfiles_images_2015_4_7_650_hwxm87zrarljzar.jpg"}},[v("img",{attrs:{src:"/pic/ggw.tongji.edu.cn_themes_59_userfiles_images_2015_4_7_650_hwxm87zrarljzar.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("■本报记者 许琦敏"),v("br"),_._v("\n【松江赵氏家训】 "),v("strong",[_._v("勤俭礼和，忠信廉诚")]),v("br"),_._v("\n今年清明，上海市政府老参事赵国通，带上弟弟中科院院士赵国屏，以及姐姐写给父亲的话，前往松江天马山，祭奠仙逝20周年的父亲赵祖康。“父亲生前曾整理赵氏家谱，并从先辈留下的叮咛和嘱咐中，合成一本小册子《赵氏宝训》，凝练出了‘勤俭礼和，忠信廉诚’的家训。”赵国通说，父亲用一生将这八个字演绎成生动的言传身教，让几近断绝的家风，在赵氏子孙中重新流传。")]),_._v(" "),v("p",[v("strong",[_._v("一代儒工，正直尽责")])]),_._v(" "),v("p",[_._v("在常人记忆中，赵祖康最突出的事迹是作为国民政府最后一任上海代市长，把上海平稳地移交给了陈毅市长。其实，对国家民生来说，他最卓著的功勋是为中国公路事业所做出的奠基性贡献。")]),_._v(" "),v("p",[_._v("“詹天佑铁路，茅以升桥梁，赵祖康公路”，研究中国交通史的人们都一致将赵祖康视作近现代中国公路第一人。他在留学回国后为中国道路标准、车牌标准、全国公路规划制定以及人才培养等方方面面做出了开创性贡献。1948年，我国著名雕塑家刘开渠曾创作雕塑《开路先锋》赠给赵祖康，以向这位中国公路界的元勋表达敬意。")]),_._v(" "),v("p",[_._v("赵祖康曾写下“开边须筑路，救国仗书生”的诗句，表达自己追随孙中山“工程救国”报效国家的心愿。中国抗战交通史上著名的四大公路，即合称为三西公路的西兰（西安-\n兰州）、西汉（西安-汉中）、乐西（乐山-西昌）三条公路，以及滇缅、汉渝等公路，赵祖康都曾为之呕心沥血。尤其乐西公路的赶工抢建，他更为之累到吐血病倒。")]),_._v(" "),v("p",[_._v("赵国通说：“父亲留学美国，但受儒家忠国爱家思想影响很深，中国历史上有儒臣、儒将、儒商，我的姐姐赵国明认为父亲可称为儒工！”他一生经历，对子女乃至孙辈的影响，十分深远。")]),_._v(" "),v("p",[v("strong",[_._v("修撰家训，家国并举")])]),_._v(" "),v("p",[_._v("赵氏世居松江，家族兴衰几经起落。1938年，赵祖康将他高祖母、曾祖父的两世分家据中，有关对后辈的叮嘱整理出来，亲自撰跋，合成一本小册子《赵氏宝训》。随后，他将《宝训》印制30本，加以编号，分发给赵氏各支族，将祖宗遗言、赵氏家训“勤俭礼和，忠信廉诚”加以弘扬。")]),_._v(" "),v("p",[_._v("“家有四训，政有四要。勤则尽忠，俭可助廉，礼而后信，和以通诚……凡我赵氏子弟，入则勤俭礼和，出则忠信廉诚，庶可重建门庭，光大祖业，效忠国家，绵绵不绝。”这段阐释，将与个人修养有关的“勤俭礼和”，与报效国家有关的“忠信廉诚”有机地联系在了一起。")]),_._v(" "),v("p",[_._v("可惜的是，《赵氏宝训》在战乱中大量流失，如今原本仅存一册，由赵祖康堂妹赵淑华女士带到台湾。1991年，赵淑华女士回沪探亲，赵国通留下了复印件。")]),_._v(" "),v("p",[_._v("“父亲之勤，从他为公路修建的殚精竭虑中，已足可见。父亲之廉，则可从我记忆中的一件小事看到。”赵国通说，他8岁那年，有客人来访，随手送了一个礼盒，打开一看，居然是一块手表！当他兴奋地告诉父母时，赵祖康勃然大怒，立刻叫来司机，让他马上把手表还回去。“当时工务局主管城建、土地、港口等，权力很大，托事说情孝敬常而有之，但父亲就坚持秉公办事，两袖清风，绝无通融。”")]),_._v(" "),v("p",[v("strong",[_._v("家风流传，子孙坚守")])]),_._v(" "),v("p",[_._v("赵氏家风的流传，赵祖康是承上启下的关键。在他影响下，三个女儿均毕业于清华、北大，二子皆于学界扬名：长子赵国通历任上海市人民政府参事，全国政协委员，上海市经委副总工等职；次子赵国屏院士是我国著名分子生物学家。其孙辈有五位取得名校博士文凭。身为全国政协委员，赵国通说，父亲的言传身教使儿女们一直牢牢记住这样的信条：“做任何事要想着为社会谋福利，而非谋私利”。赵国屏院士在开拓科研新领域的同时，对科研体制中存在的种种问题，经常给出精辟的见解，并积极推动改善。")]),_._v(" "),v("p",[_._v("清明祭扫人车拥挤，而赵氏子孙散布世界各地，由此他们常在网上表达对先人的哀思怀念，然后由赵国通夫妇为代表，前往赵祖康墓前献祭。赵国屏说，对先人最好的纪念，就是牢记家训，绵延家风，通过自身的一言一行，将和风正气在社会上得到更多弘扬。")])])}),[],!1,null,null,null);v.default=p.exports}}]);