(window.webpackJsonp=window.webpackJsonp||[]).push([[932],{1293:function(v,_,p){"use strict";p.r(_);var t=p(14),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("hr"),v._v(" "),_("p",[v._v("一代儒工与西汉公路建设")]),v._v(" "),_("p",[v._v("2014年05月07日 08:07 来源：东方早报 作者 赵国明")]),v._v(" "),_("p",[_("img",{attrs:{src:"http://www.cssn.cn/lsx/lsmr/201405/W020140507399718063970.jpg",alt:""}})]),v._v(" "),_("p",[v._v("1950年代的赵祖康")]),v._v(" "),_("p",[_("img",{attrs:{src:"http://www.cssn.cn/lsx/lsmr/201405/W020140507399870792810.jpg",alt:""}})]),v._v(" "),_("p",[v._v("1936年西安-汉中铁路劈山筑路现场（张佐周摄）")]),v._v(" "),_("p",[v._v("一代公路专家赵祖康与蜀道关系可谓密切。从1935年主持修建西兰（西安-兰州）、西汉（西安-\n汉中）等公路开始，在赵祖康的主持下，公路修建方保护了陈仓道、褒斜道等大量文物古迹。《东方早报·艺术评论》特刊出赵祖康女儿回忆其父亲的《一代儒工》节选。")]),v._v(" "),_("p",[_("strong",[v._v("作者：")]),v._v(" "),_("strong",[v._v("赵国明")])]),v._v(" "),_("p",[v._v("** 忧国浑忘病 筹边不顾家**")]),v._v(" "),_("p",[v._v("从1935年主持修建西兰（西安-兰州）、西汉（西安-汉中）等公路开始，父亲（赵祖康）一步步攀上了一生事业的最高峰，他的诗篇创作也随之进入了旺盛时期。")]),v._v(" "),_("p",[v._v("视察西北公路道中（1935年）")]),v._v(" "),_("p",[v._v("无分秦淮棹，征车逐土沙。轮飞晨梦转，云叠暮愁遮。忧国浑忘病，筹边不顾家。会看连朔漠，塞外听胡笳。")]),v._v(" "),_("p",[v._v("好诗。父亲自比战士辞家远征，国运危殆，他忧心如焚。")]),v._v(" "),_("p",[v._v("目击当地民生凋敝的景象，父亲感慨万千：")]),v._v(" "),_("p",[v._v("华家岭道中（1935年）")]),v._v(" "),_("p",[v._v("驱车岭道若登天，万壑千山四野连。草共土黄无一树，村随谷转剩孤烟。梯田犹见先民乐，古堡空教壮士怜。莫怪班生投笔去，不堪点缀入诗篇。")]),v._v(" "),_("p",[v._v("除了具有重大的经济军事意义外，西汉公路也是当时保护文化遗存做得最好的工程。大散关因陆游的诗句“楼船夜雪瓜州渡，铁马秋风大散关”而闻名天下。陆游作为一介书生，在大散关抗金，而今父亲等人在此修路，也是为了抗日需要。历代爱国的知识分子就是以自己的行动为“救国仗书生”做了最好的证明。父亲也因而在此题字“古大散关”。还有，据张良庙博物馆的工作人员说，原来公路选线不必经过此庙，为了展示此古建筑群，特意将路线绕经庙前，从此，张良庙成了西汉路上一道耀眼的风景。抗战时期众多名人到此驻足，故宫博物院的文物也从这里运送至大后方。数十年后，父亲在《旧中国公路建设片段回忆》一文中有一段话：“……从西汉公路宝鸡、汉中之间整个路线情况看，除南北两端……15公里是一片原野，地势平坦之外，其余路线都在秦岭山脉中曲折盘旋，极为险阻。过益门镇进入山口，穿过大散关，从秦岭山麓须绕11公里的盘山路线才到达岭顶。往南从凤县东门到留坝东门的82公里，群山夹峙，只有一径中通，险窄崎岖。这一段要越过两处山岭：一处是酒奠梁（传刘邦经过梁顶时曾用酒祭奠）……另一处是柴关岭……这一段路线旧址就是古代‘明修栈道，暗渡陈仓’的北栈道，沿河的岩石上留有古代凿洞架木铺筑栈道的许多榫眼，其中画眉关……等处都是峭壁陡崖，下临深谷，地势十分险峻。尤其以褒城北2公里的鸡头关，隔褒河与石门（峭壁上刻有‘石门’二字）对峙，形成壁立的峡谷。山下褒河中有两块圆石，刻有‘玉盆’、‘衮雪’四个大字，相传是魏王曹操的亲笔。这一路段古迹较多，我和孙发端、张佐周等商量，既要把工程做好，又要妥善保存……古迹……（为此，将）原沿褒河西岸修筑，拟在褒城过河（的路线）……改在鸡头关上游建桥过河，经石虎山脚处开凿了两个山洞，全长66米……”可见，当时为了保护众多意义重大的国宝级古迹，公路特地改线。建成后父亲又隆重邀请交通界老前辈又是大书法家叶恭绰老先生在新开的山洞口题字“新石门”。可以说，父亲和那一辈公路人也是中国“公路文化”的奠基人。")]),v._v(" "),_("p",[v._v("父亲撰写此文的确切时间不详，但收录此文的《文史资料选辑第83辑》则出版于1980年代。当时的父亲大概并不知道，由他和一群公路工程人员在上世纪30年代精心呵护而得以完整保存下来的世界级文化遗产，早在三十余年后的1970年前后，就已经因为修建一座水库而被彻底毁坏了。由石门隧洞（世界上人工开凿的第一条通车隧道）、褒斜古栈道（演绎过无数英雄故事历史传奇的地方）以及成百件摩崖石刻（展现了汉魏至宋历代最精湛的书法真迹）等等构成的两千余年绵延不断的中华文化长廊，尽皆淹没于浩渺大水之中。对于当时“革命”的修建水库的决策者来说，“死人要给活人让路，不能让几块石头把路挡住”，坝址决不移动。全仗有识之士的拼死奔走呼吁，才有少数几块石刻（《石门十三品》，又名《汉魏十三品》）被切割下来，抢救性地送进了博物馆，得以幸存至今，如今已被公认为世上不可复制的文化瑰宝。当张佐周，当年正是留坝至汉中路段工程的负责人，为保护古迹而特地改变路线的大功臣，在数十年后再返西汉路时，看到万顷波涛夺走了千古文采，不由得惊愕失色。痛惜之余，怅然嗟叹，他说，真要建水库，只要将库址上移若干距离，不是什么都解决了！2005年，张佐周逝世，家人遵照他的遗嘱，将他的骨灰安葬在了汉中，他将永远守望着那条他无比珍爱、曾经为之付出了巨大心血的西汉公路！")]),v._v(" "),_("p",[v._v("据《功在千秋》一文注释，以上为幸存的十三品中的两品。“石门”二大字摩崖，原址在石门北口西壁，通高82厘米，宽50厘米，字径35厘米，二字竖列，字迹笔画十分清晰。古褒斜栈道南出褒谷，为鸡头所阻。东汉明帝下诏开凿穿山隧道，盖在汉时，尚无隧道之名，乃以“石门”喻之，后即以此为隧洞命名。“门”字东西两竖笔有“八”向分开之势，如援弓待发，气魄昂然。")]),v._v(" "),_("p",[v._v("隶书“衮雪”二大字摩崖，原在汉水褒谷古石门以南的激流中。褒水纯净，上下落差较大，水流湍急，加之受乱石遏阻，喷沫飞溅如滚雪之状。相传曹操见此胜迹，乃书“衮雪”二字以喻之。细观“衮雪”二字，其笔近篆而体近隶，行笔纵放不羁，确有波涛澎湃之势。“衮雪”二字的风韵真切地描绘出了曹操的神态和魏武精神。此处的“衮”字等同于“滚”。")]),v._v(" "),_("p",[v._v("不但对前人的文化遗存尽力保护，父亲本人在建设公路时，所到之处，也颇爱题字写诗。这里介绍他很另类的一组诗，是四首五言绝句的怀古诗，创作于1935年视察西汉路工程之时，当时就题写在张良庙内北花园墙壁上。诗墙在“文革”中被毁，幸有热情的好事者根据手抄本复原，并于1982年与其他被毁诗文一起收录成册，印刷出书。遗憾的是，复原的诗作有多处较明显的错漏。在此，除了改正错别字外，我将第一首诗中严重缺损的第一句补上开头四个字，但求能恢复作品的完整性，并符合父亲原意。")]),v._v(" "),_("p",[v._v("无题")]),v._v(" "),_("p",[v._v("云间 赵祖康，二十四年十一月")]),v._v(" "),_("p",[v._v("檄羽催征急，家书传递迟。何时度辽在，两万整雄师。")]),v._v(" "),_("p",[v._v("耳语机何密，箸谋遇岂迟。就刘犹孺子，不愧帝王师。")]),v._v(" "),_("p",[v._v("归汉非移志，宁甘万户侯。赤松仙不返，紫柏迹长留。")]),v._v(" "),_("p",[v._v("斩蛇成帝业，屠狗亦封侯。椎击真开志，姓名惜不留。")]),v._v(" "),_("p",[v._v("根据传抄本的注释：")]),v._v(" "),_("p",[v._v("“度辽”：汉将军名号，以渡辽水为义。汉昭帝置度辽将军，将兵击辽东乌桓。后汉复置之，防护匈奴。")]),v._v(" "),_("p",[v._v("“两万整雄师”：可能指参筑西汉公路的人数。")]),v._v(" "),_("p",[v._v("“斩蛇成帝业”：指刘邦。")]),v._v(" "),_("p",[v._v("“屠狗亦封侯”：指樊哙原是卖狗肉的，后因战功封舞阳侯。")]),v._v(" "),_("p",[v._v("中国历代的读书人对儒道两家从不任意取舍褒贬，相反，而是更看重两家相辅相成之用。至于从儒还是从道，往往视某时某刻的客观形势与个人处境而定，即所谓“进退有度”（《礼记》）。儒者总以天下为己任，矢志肩担道义，匡时济世；而道者更看重自身的清白，追求超脱凡尘，回归自然。张良是儒道兼备的历史上的楷模。父亲对张良的智慧才能、风格品质的钦佩赞美，多少折射出父亲本人的抱负和理想——发扬蹈历、尽智索能、建功立业、报效国家，而后及时引退、归隐山林。但看来父亲毕其一生只是个儒者，他摆脱不了尘世的羁绊。这一方面说明了他始终不渝的报国精神的可敬可佩，但另一方面，以我个人之见，这也使父亲的生命色彩显得单调了许多。如果他在晚年能稍稍避世，超脱一些，我想，他的人生会更加丰富多彩。")]),v._v(" "),_("p",[_("strong",[v._v("久分此身已许国")])]),v._v(" "),_("p",[_("strong",[v._v("生死从来最自由")])]),v._v(" "),_("p",[v._v("1937年抗日战争全面爆发。八一三战事打响时，父亲让母亲带着奶奶、大姐和我乘轮船先行逃难，离开南京。此后的数年中，我们为躲避战火四处漂泊，栖身于多个城镇乡村，生活疲劳困顿，等再见到父亲，已经是八年以后的事了。")]),v._v(" "),_("p",[v._v("此时，父亲还坚守在南京的政府机关工作。下面是他的抗战时期第一诗：")]),v._v(" "),_("p",[v._v("守国吟（1937年）")]),v._v(" "),_("p",[v._v("京国五年忍见侵，蓼花枫叶寄丹心。枭机浪掷千枚弹，一卷指南自在吟。")]),v._v(" "),_("p",[v._v("父亲注：入地下室时辄携《文文山集》。")]),v._v(" "),_("p",[v._v("父亲肯定不止一次诵读了文天祥的《正气歌》，在对侵略者的斗争中，他已做好了流血牺牲的准备。")]),v._v(" "),_("p",[v._v("淞沪战一直坚持了近3个月。11月12日，上海失守。11月19日深夜，父亲奉命自南京紧急撤离，奔赴中央政府临时驻地武汉。当时的他，真是悲愤交加：")]),v._v(" "),_("p",[v._v("出都吟")]),v._v(" "),_("p",[v._v("一九三七年十一月风雨之夜，奉秦秘书长之嘱，搭全国经济委员会放小汽艇济农号出都赴武汉，泫然赋此同行。")]),v._v(" "),_("p",[v._v("机轮摩托响呜呜，千古金陵一掷孤。前线战士争杀敌，中朝大吏竞迁都。曾听慷慨追文史，却看仓皇作秀夫。我亦今宵同出走，孤舟风雨叹前途。")]),v._v(" "),_("p",[v._v("到了武汉，父亲立即投身处理适应战事需要的公路抢修事宜。1938年，他去西北视察。在西安时，给同行的同事们写了一首诗：")]),v._v(" "),_("p",[v._v("寓西安西京招待所示同行诸子")]),v._v(" "),_("p",[v._v("危崖急坂逞豪游，生死从来最自由。久分此身已许国，诸君何事戒凶楼？")]),v._v(" "),_("p",[v._v("这是一篇佳作。我猜想，某些一同出差的人大概觉得入住的处所易遭敌机袭击，想换个较安全的地方。父亲出自肺腑的豪言增长了众人的勇气，壮了大家的胆，而他随时准备为国献身的表白也绝非夸夸其谈。当时，在大后方修建公路，不但要吃苦受累，同样要冒矢石之险，有性命之虞，以后也确实发生了工程技术人员以身殉职的事。")]),v._v(" "),_("p",[v._v("视察期间，父亲过大散关，豪情勃发，寄大姐长诗一首。大姐当时才九岁（大名赵国聪，解放后改名赵充），但已非常懂事，文才极佳。父亲赠诗给她，想必认为她能理解他的事业、抱负和爱国爱家的精神。")]),v._v(" "),_("p",[_("strong",[v._v("过大散关寄阿聪")])]),v._v(" "),_("p",[_("strong",[v._v("1938年5月西汉道中")])]),v._v(" "),_("p",[v._v("山苍苍，云茫茫，秦岭之高不可当。鸟飞不得度，猿攀不敢望。成路一百又八转，轻车片刻上高冈。山峻出奇峰，云飞起大风。三月凿通千里路，当年意气不凡庸。工夫两万冒霜雪，戍兵百廿护交通。电台电话消息捷，飞刍挽粟马如龙。一朝通车到南郑，万人空巷竞道夺天工。我今重过散关下，旧题勒石认钩画。男儿识字果何为，放翁工部徒诗客。长吟寄与阿聪歌，应念风尘两鬓白。")]),v._v(" "),_("p",[v._v("父亲题字“古大散关”是在1936年，1938年又见刻石，所以有“我今重过散关下，旧题勒石认钩画”的诗句。“文革”后一度传闻刻石已遭破坏，现已发现，保存完好。父亲在武汉沦陷前五天还在当地指挥抢修被日寇炸毁的公路，极力维持战时运输的畅通。武汉是1938年10月失守的，说明父亲大概也就在那个时候去了重庆——国民政府的临时首都，并在交通部一干就是七年，抗战胜利后才到上海任新职。")]),v._v(" "),_("p",[v._v("1939年，母亲托人将奶奶和我们三姐妹送到香港，随二舅一家转到尚未陷入日寇统治的上海法租界定居，只身一人去了重庆陪伴父亲。从此，我们又与母亲阔别了六年。")]),v._v(" "),_("p",[v._v("1937年避难长沙时的家庭合影，有祖母、母亲和我们三姐妹，父亲当时应在武汉。■")]),v._v(" "),_("p",[v._v("（本文系节选，标题为东方早报编者所加。）")])])}),[],!1,null,null,null);_.default=s.exports}}]);