(window.webpackJsonp=window.webpackJsonp||[]).push([[934],{1297:function(_,v,t){"use strict";t.r(v);var p=t(14),r=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("|  |  |  |  |")]),_._v(" "),v("h1",{attrs:{id:"赵祖康日记里的1949"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#赵祖康日记里的1949"}},[_._v("#")]),_._v(" 赵祖康日记里的1949")]),_._v(" "),v("p",[v("a",{attrs:{href:"http://www.jfdaily.com/shanghai/bw/201405/t20140513_336115.html#%E2%80%9D",target:"_blank",rel:"noopener noreferrer"}},[v("OutboundLink")],1),_._v("来自:解放日报 |\n2014-05-13 09:37 | 作者：梁建刚 朱晨 口述：赵祖康之子赵国通")]),_._v(" "),v("p",[_._v("旧上海末任代理市长赵祖康在日记里写下：这一年亲历翻天覆地。")]),_._v(" "),v("p",[_._v("见到本报专题报道征集，赵国通激动地与本报联系并翻开一本父亲日记")]),_._v(" "),v("p",[_._v("这一年，他亲历翻天覆地……")]),_._v(" "),v("p",[_._v("——上海解放前末任市长赵祖康日记里的1949")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://newspaper.jfdaily.com/jfrb/files/20140513/m_742498.jpg",alt:""}})]),_._v(" "),v("p",[_._v("1949年6月，陈毅市长和赵祖康（持帽者）等视察浦东海塘。 本报资料照片")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://newspaper.jfdaily.com/jfrb/files/20140513/m_742500.jpg",alt:""}})]),_._v(" "),v("p",[_._v("赵祖康1949年的日记本。 李谧欧 摄")]),_._v(" "),v("p",[_._v("解放日报编者按 今年是上海和全国解放\n65周年。留住记忆，还原历史，发现价值，本报自5月4日起，向社会各界征集“上海，我的1949”专题报道线索。短短一周，热心读者们纷纷来电来信，提供线索与故事。他们中，有参与过解放上海战役的老战士，有当年的地下党，有本报老通讯员，也有重要历史人物的后代……今天，本报刊登的第一篇，就是旧上海最后一任代理市长赵祖康之子赵国通提供的。家人最近整理遗物时，意外发现一本父亲在1949年写下的完整日记。见到记者，赵国通拿出一个手掌大小的长方形皮夹，咖啡色，近乎发黑，小心翼翼从里面拈出一本同色笔记本，封面上是“diary”（日记）。他一边翻开日记，一边动情讲述，那个他与父亲一起经历过的、永远留在记忆深处与父亲笔下的——上海1949：“这一年，他亲历翻天覆地……”")]),_._v(" "),v("p",[_._v("口述：赵祖康之子赵国通")]),_._v(" "),v("p",[_._v("采访整理：本报记者 梁建刚 朱晨")]),_._v(" "),v("p",[_._v("那一年，我9岁。所以感谢父亲，竟然从天而降了这样一本日记，从年头一笔一画记到年尾。里面写的许多事啊，我也从不知道，也觉十分新鲜。那一年的上海呵，也更生动、更丰满、更加清晰了。")]),_._v(" "),v("p",[_._v("来，你们看，第一页、第一篇，写的就是1月1日元旦，万象更新，可他的心情，却和当时很多上海市民一样，很复杂、很纠结。一方面，他这天“赴各师友处贺新年……谈甚畅”，但日记中，所有这些“欢畅”的谈话，最后总离不开一句反复出现的——“希望和平”。另一方面，这天各地报纸上，有蒋介石元旦文告“和战关键系于共党”，有毛泽东新年贺词“将革命进行到底”，也有我父亲发表在当时中央日报上的一篇\n《对于上海工务建设之新年愿望》，这是他心心念念的梦想，凝聚着历时3年对大上海建设的规划，希望彻底打破上海由列强租界各自为政的城建格局，使这个繁华却布局凌乱的城市，成为一座美丽的条带状世界级大都会。然而在当时的上海，这种愿望只能是缘木求鱼。所以早在几个月前，我父亲就将我奶奶、我母亲和我们3个孩子，一起送往福州避难。当时心情，可想而知。")]),_._v(" "),v("p",[_._v("1949年第一天的上海，舞照跳。元旦的青年舞会，我们的大姐还盛装出席。短短10天后，形势就大变了。1月10日淮海战役结束，当天蒋经国就秘密飞沪，夜里，上海中央银行金库的黄金，十万火急，被秘密装船运往台湾。这一天，上海的经济已接近崩溃边缘。曾有报道抨击说：上海市面上的算盘已从传统的13档加长到17档，若钞票还要继续跌落，等到我们的算盘加长至27档，我们的写字台也要不适宜运用而必须放大，写字间的面积也要扩大，至此房荒就更严重了……就在这一天，你们看，1月10日，日记写了，我父亲还在“为机器进口与免费事”奔走，也在和友人的叙旧中，唏嘘不已：“人生诚不啻一梦，昨见胡山源，一别12年亦。”")]),_._v(" "),v("p",[_._v("唉，再往下看。20多天后，2月4日，我父亲依然在开“市政会议，赶修桥梁”，下午“都市计划会议讨论三稿”……不过，注意看！这天日记提到一位\n“曹石俊钱挹珊请吃饭晤见朴君及李小姐”中的“李小姐”。记者同志，请你们一定要提到这位隐蔽战线的李小姐，我父亲对她非常感激。正是她，将我父亲与上海解放联系了起来，从此开始接受党的指示，她的名字叫王月英。瞧！上海1949年的头一两个月，进步的火种就开始更多地撒进了大上海。这本日记有好多类似记录。看这里，1月24日，“阅沈志远近代经济思想史”，你们知道，沈教授就是著名的马克思主义经济学家。1月29日，父亲又“夜听陕西电台广播”。等到3月25日，“中国工程学会代表侯德榜、茅以升、恽震、顾毓瑔及余五人，谒见李宗仁总统，请求通令军队不得破坏工矿交通公用地质及设备……”这便是轰动一时的中国工程师学会五代表赴南京请愿事件，我父亲是主要策划者之一。26日，“余等五人晋见何应钦院长”。27日，索性“在侯德榜处商议，另致毛泽东主席文，作目标之呼唤……”")]),_._v(" "),v("p",[_._v("我能感觉到父亲当时的心情——希望，已经寄托给新中国。")]),_._v(" "),v("p",[_._v("5月23日这篇有段记载：深夜，“陈（良）市长邀赴市府坚嘱……由余代理市长职务……”父亲一生最难忘的五天四夜来了。那天，上海解放已经只是时间问题，原本代理市长的陈良把我父亲推上前台。")]),_._v(" "),v("p",[_._v("我父亲也早有打算：地下党多次指示设法接手市政机关，配合大军解放上海。推辞一番后就任，他连日通宵达旦“维持秩序，办理移交”。24日夜至25日，解放军占领苏州河以南整个地区，我父亲指示各警察局不要做任何抵抗，缴械投降；同时与各局处主要负责人集中办公，拜见中共接收代表，抢修急要工程、维持治安、保管档案、恢复交通……解放上海期间，全市没一天停电停水，城市秩序最快速度恢复，市民安然无恙。苏州河北岸邮政大楼，有国民党青年军固守，我父亲在解放军指导下，电请邮政局长劝降，数小时磋商成功，历史建筑完好保存。所以这天的日记，你们看，我父亲难得地写得比较多、抒了一次情：“当日清晨，苏州河南已告稳定。我到市府大厦，看到已竖起白旗，大门口架放着原警卫市府大厦的枪支，内心很平静高兴。”我想，这也是那天不少市民的心情。")]),_._v(" "),v("p",[_._v("对了，翻天覆地那几天，还有篇日记很重要。战事27日结束，但纪念日定在28日，就因市府政权交接这件事。具体时间，我父亲后来撰文回忆，应是下午2时陈毅等来正式交接，3时举行旧市府职工大会。陈毅市长让大家安心固守岗位，将来量才适用，局面很快稳定下来。我父亲想办好移交后，去大学教书。陈毅市长摇摇头，鼓励他发挥专长，为市政建设出力；过两天又专门谈心，请任市工务局长。我父亲非常感动，打消顾虑，当即答应。特地在那天日记写下：“陈表示好感，要我继续工务局长，‘我们一定很能合作的’。”")]),_._v(" "),v("p",[_._v("陈毅市长与新中国对科学人才的重视，让我父亲分外感动。所以后来，你们看，这日记里一篇一篇，从年中，到年底，满满的全是工作。6月6日，他连日赶工修改的“上海市大都市计划三稿草图”，递交并得到市政府批准；七八月，百年罕见的台风袭击，浦东高桥炮台浜决口，他陪陈老总一线视察；10月国庆大典时，他还在工作，商议海塘事；12月，最后一篇日记是“28日，上海市政公会工务分会成立大会……五千人的大家庭\n（工务局有5000职工）”，看得出，他极高兴。")]),_._v(" "),v("p",[_._v("一页页一篇篇，这一年就这么过去了。现在，65年也过去了。上海，我的1949！这一年开始时，我父亲还是一名在旧政府备受排挤的技术官员，后来在关键时刻，他做了正确的决定，尽到自己的职责。这一年结束时，他已经成为新中国一名人民公仆。我们永远为父亲的选择骄傲！")]),_._v(" "),v("p",[_._v("人物档案")]),_._v(" "),v("p",[_._v("赵祖康（1900年—1995年），松江县人，道路工程专家、市政规划专家，与詹天佑、茅以升并称为中国交通工程三杰。抗战胜利后任上海市工务局局长、代理市长。新中国成立后，任上海市人民政府委员、工务局长，副市长、市政协副主席等职，1982年当选民革中央副主席，1995年在上海病逝。")]),_._v(" "),v("table",[v("thead",[v("tr",[v("th")])]),_._v(" "),v("tbody")])])}),[],!1,null,null,null);v.default=r.exports}}]);