(window.webpackJsonp=window.webpackJsonp||[]).push([[935],{1295:function(_,v,p){"use strict";p.r(v);var t=p(14),a=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("|  |  |  |  |")]),_._v(" "),v("p",[_._v("公路工程与市政工程专家赵祖康")]),_._v(" "),v("p",[v("strong",[_._v("（来源 ／ 百度百科）")])]),_._v(" "),v("p",[_._v("赵祖康，毕业于交通大学唐山学校（今西南交通大学），公路工程与市政工程专家。在本世纪30年代初，即从事于中国公路的创建事业，为发展现代公路奠定了基础。中华人民共和国成立后，长期担任上海市市政建设领导工作，主持制定了上海市都市规划，提出了发展蓝图。他重视科学，尊重人才，培育了很多科技骨干，为新中国的公路和市政建设做出了卓越的贡献。他长期致力于道路、交通辞典的研究和编写工作，并为此做出了重要成绩。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_HNZ9wRcKZDAeIPiEbZ8ULw==_3724476891852570787.jpg",alt:""}}),_._v("赵祖康")]),_._v(" "),v("p",[_._v("1905-1918年 求读于松江小学、中学。")]),_._v(" "),v("p",[_._v("1918-1922年 考入上海南洋大学（今上海交通大学、西安交通大学），后转入交通大学唐山学校（即西南交通大学），毕业获学士学位。")]),_._v(" "),v("p",[_._v("1924-1925年 任南京河海大学教员兼秘书。")]),_._v(" "),v("p",[_._v("1925-1927年 任上海交通大学助教兼秘书。")]),_._v(" "),v("p",[_._v("1927年 任广东建设厅公路处技士。")]),_._v(" "),v("p",[_._v("1927-1929年 任梧州市工务局技正兼设计课课长、局长。")]),_._v(" "),v("p",[_._v("1930年 由铁道部派赴美国康奈尔大学研究院研究道路与市政工程。")]),_._v(" "),v("p",[_._v("1932年 任铁道部工务处技士。")]),_._v(" "),v("p",[_._v("1932-1938年 任全国经济委员会公路处专员，道路股股长、副处长、代理处长。")]),_._v(" "),v("p",[_._v("1938-1941年 任交通部公路总管理处处长。")]),_._v(" "),v("p",[_._v("1943-1944年 任交通部公路总局副局长。")]),_._v(" "),v("p",[_._v("1945-1949年 任上海市工务局局长、兼都市计划委员会常务委员会及执行秘书，上海市代理市长。")]),_._v(" "),v("p",[_._v("1949-1951年 上海市人民政府工务局局长，兼任上海市人民政府委员。")]),_._v(" "),v("p",[_._v("1951-1954年 任上海市人民政府市政建设委员会副主任委员兼工务局局长。")]),_._v(" "),v("p",[_._v("1954-1957年 任上海市规划建筑管理局局长。")]),_._v(" "),v("p",[_._v("1957-1967年 任上海市副市长。")]),_._v(" "),v("p",[_._v("1979-1983年 任上海市副市长。")]),_._v(" "),v("p",[_._v("1983年 任上海市人大常委会副主任。")]),_._v(" "),v("h1",{attrs:{id:""}},[v("a",{staticClass:"header-anchor",attrs:{href:"#"}},[_._v("#")])]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_J_L5EdrQLBlhMrNSypQ2DQ==_4854598923345869497.jpg",alt:""}}),_._v("家庭")]),_._v(" "),v("p",[_._v("赵祖康，字静侯。出生于江苏省松江县城厢镇。赵祖康2岁时，父因病逝世，母子两人相依为命。自幼就嗜学好问，喜听大人读书声，并朗朗仿诵，好问中国古代的英雄事迹。岳飞、文天祥等故事，在他幼小的心灵上，种下了爱国主义的种子。母亲见他好学，5岁就送他去私塾读书，7岁进了新式松江泖秀小学，8岁转崇文小学，以成绩优异跃升华亭县立第一高等小学。他在校认真刻苦攻读，尤擅长作文，老师称他有文学天才，曾参加全县征文比赛，获得金质奖章。14岁考进江苏省立第三中学，课余喜读革命书刊，在书桌上贴一个纸条：“勤于读书，爱国爱民”以自勉，领悟到读书和国家民族的关系。18岁毕业后，考进了南洋大学（现上海交通大学、西安交通大学前身），1921年，转入唐山交通大学（今西南交通大学）攻读土木工程，将“致力工程，为民服务”，作为终生奋斗的目标。")]),_._v(" "),v("p",[_._v("他以交通建设为重点，曾写了“开边须筑路，救国仗书生”的豪迈诗句。进入南洋大学的第二年，即1919年，全国爆发了“五四”爱国学生运动，赵祖康即投身于热火朝天的运动之中，义愤填膺，奔走呼号，主编刊物，宣传反帝爱国思想，提倡新文化、妇女解放。曾编写话剧“李超群的终身大事”，描述女青年，不应以毕业结婚，养儿育女为终生目的，应当继续学习，谋求妇女的解放。张家惠主演，由此而与张家惠相熟识，后于1924年结为伉俪。五四运动后，他认为救国必须有真才实学，自勉“读书不忘救国，救国不废读书”作为座右铭。1922年，以出众的成绩毕业于交通大学唐山分校（今西南交通大学）土木系。")]),_._v(" "),v("h1",{attrs:{id:"-2"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#-2"}},[_._v("#")])]),_._v(" "),v("p",[_._v("毕业后曾在武汉、梧州，蚌埠各地工作。1930年，由铁道部派去美国康奈尔大学研究院，进修道路与排水工程，并在纽约州奥伯奈市州公路处任实习工程师，由此获得了不少实际经验。1932年，学成回国，进入全国经济委员会筹备处，担任道路股股长，直到1945年。在13年工作生涯中，在中国公路建设史上，写下了重要的一章。他担任股长时期，只一年光景，就显示出他的渊博的学识、高超的才能和统筹全局的本领。当时的中国公路总长度不过2万余公里，且各省各自为政，互不联系；工程技术缺乏标准；有路面者不多，以致晴通雨阻；桥梁多为木质临时式的，大江大河多用人工摆渡；经费无固定来源，管理缺少规章制度；汽车无统一的牌照，不能过境行驶，技术水平较低，也无培训组织。赵祖康目睹中国公路的落后情况和与世界先进水平的差距，立志于有所作为成为他青年时期的目标。在以后的岁月里，他的工作机构虽几经改变，自全国经济委员会转到交通部，由道路股扩大到公路处，公路总管理外，直到公路总局。他的职位由股长、副处长、处长到副总局长，但他一贯耿耿于公路事业。为了改变各自分割的局面，他主持省际联络公路的筹建和督修，联络公路由3省到7省，扩大到10省；制定了筑路经费的补助办法；成立了督察区，监督工程用款、工程质量和进度；颁发了统一的工程技术标准；提倡就地取材修筑路面；逐步改善桥渡；统一汽车牌照，建立全国各省市交通互通和公路监理制度；进行交通安全宣传等。赵祖康在主管公路时期，十分重视公路交通人才的培训，成立了各种培训班，选派工程师出国进修实习。他尊重科学，引进先进技术，主张理论联系实际，“筑路带动试验，试验促进施工”，说到做到。他努力钻研公路选线，提出了精湛的见解。对公路工程学名词的译法和确定，下了很大功夫，十分重视修路与地质的关系。截至1937年抗日战争前夕，全国公路里程增加了8万余公里，这都是直接或间接地与赵祖康的辛勤努力分不开的。抗日战争爆发后，他组织力量赴前方抢修公路，又加紧筹划西北和西南国际补干线公路的修筑，奋不顾身地奔波在蜀道天险之间和黄土高原之上，督导施工，不遗余力。曾因劳累致疾，写下了“久愿风尘殉祖国，宁甘药饵送余生”的诗句。在当时有功反而受谤，他曾被投闲置散，担任交通部顾问之职，此时即努力于编写公路工程规范工作。抗日战争胜利后，他到上海担任工务局局长，想以自己的所学为乡梓服务，奈以政治腐败，国穷民凋，难以有所建树，对上海的改造，只得寄希望于纸上的规划。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_yRxmauuBEdxMpRCNCNokWQ==_3724476891852570788.jpg",alt:""}}),_._v("致力于研究")]),_._v(" "),v("p",[_._v("1949年5月，上海解放前夕，他曾担任了7天的代理市长，对维持社会的治安，保护人民的财产作出了重大的贡献，得到了当时陈毅市长的嘉许，并即委派他担任上海人民政府工务局局长。就任之初，上海遭到台风暴雨的袭击，浦东海塘多处决口，市区积潦，损失严重，赵祖康在陈毅市长的率领下，亲赴海塘指挥堵口，终获成功。为了贯彻城市为劳动人民服务的方针，在棚户区，修治道路，埋设沟管，改善环境条件，又感于上海道路网布局不合理，辟通了天目路和长寿桥，以加强东北和西北工业区的联系，开辟河南南路和西藏北路，打开南北交通。以后曾任上海市规划建筑管理局局长，上海市人民委员会副市长，现任上海市人大常委会副主任。曾任全国人大第一、二、三、四、五、六届代表，上海市政协第一、二、三、四届常委、五届副主席。在学术团体方面：历任中国公路学会名誉理事，中国土木工程学会第二、三届副理事长，上海市土木工程学会名誉理事长，上海市交通工程学会名誉理事长，在政治团体中：任民革中央副主席，上海市民革名誉主席等职。")]),_._v(" "),v("h1",{attrs:{id:"-3"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#-3"}},[_._v("#")])]),_._v(" "),v("p",[_._v("西兰公路，自西安到兰州，全长706公里，大部分在黄土高原之上，沟壑纵横，高低之间，相差达2300余米，原有道路坡陡弯急，行车异常危险；有山无石，有沟缺水，施工十分困难。赵祖康亲自上路，安排指导，根据缓急先后，采取分期通车办法。他大胆采用土质拱桥和挡土墙，节省了大量资金。为了解决修筑路面所需的砂石材料短缺的问题，曾在西安附近修建水泥稳定土试验路，但因当时无专用筑路机械，以致未能推广。该路采用边通车边施工的措施，于1935\n年全线畅通。在后来抗日战争中，作为西北国际补给运输干线，发挥了很大的作用。西汉公路，由西安到汉中。新修工程由宝鸡到汉中，长254公里。该路所经之地，就是古蜀道的北栈道部分，大诗人李白曾有：“蜀道之难，难于上青天”的名句，足见其艰险程度。赵祖康从选线到施工，无不亲自过问，该路自1934年底动工，1936年5月抢通。秦岭的越岭线，回旋舒展，“鸡头关”沿溪线的雄伟壮观，加上跨越褒河的钢桥，和凿穿嶂壁的连环三洞，风景幽胜，获得了专家的好评。")]),_._v(" "),v("p",[_._v("乐西公路，自四川省乐山至西昌，全长525公里。经过海拔2800米的蓑衣岭和波涛汹涌的大渡河。在地形险恶、气候多变、人烟稀少、给养困难的复杂条件下，赵祖康率领全路职工，与天斗、与地斗、与人斗，用了一年五个月的时间，于1940年底打通，其困难远远超过了单纯公路工程，可以说是一项庞大的系统工程。赵祖康不仅是一位工程技术专家，而且是一位全能的组织者，他在主持乐西公路工程3年时间里，由于昼夜操劳而患咯血，生病疗养于成都。")]),_._v(" "),v("p",[_._v("道路工程作为一门学科，属于应用科学，它不是专靠理论来指导，更重要的是科学实验。赵祖康在工作中，始终坚持这个主张，他在1932年主持公路建设的开始，就推动了两条试验路：“第一条试验路”位于南京麒麟门至马群镇之间，共布置31种不同类型的路面，本着就地取材的方针，以国产材料为主，计有块石、碎石、砖块、水泥混凝土“轨道”、竹筋水泥混凝土等，以观察经过通车后的优劣成败。“第二条试验路”位于南京中山门外孝陵卫附近，以试验沥青表面处治为主，因为当时尚无国产沥青材料，采用不同牌号，不同种类的进口沥青和不同的操作方法，分段进行，共铺筑长度1\n650米的路面，其目的是为了提高路面等级，探索最佳材料和施工方式。他编写了“全国经济委员会第一试验路报告”，在1934年，第二次参加国际道路会议时，作为论文发表。1941\n年，主持修筑乐西公路，虽然期限紧迫，仍然成立路面工程事务所，负责试验新型级配路面并推广了62公里。以后又和昆明西南联大、重庆中央大学、乐山武汉大学合作进行各种路基路面问题的研究，取得了优良的成果。")]),_._v(" "),v("h1",{attrs:{id:"-4"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#-4"}},[_._v("#")])]),_._v(" "),v("p",[_._v("赵祖康的治学，非常重视基本功，他对道路交通名词的审订和注释，费了一生的精力。早自1929年开始即着手于这项工作的研究，曾著有《道路工程学名词译法之研究》，提出了原则性的意见，归纳成为二十一律，对译名具有创造性的见解。后来虽公务繁忙，但研究从未中断。1960年，他在担任上海市副市长期间，组织了28位专家、教授，自任主编，编辑《英汉道路工程词汇》。他在编写过程中，对一词一字，都经过反复推敲，直到满意为止。经过4年的努力，终于在1965年6月，由人民交通出版社出版，后又经过修订和补充，1978年再版，受到了国内外的重视，联合国教科文组织曾指定它为专业参考工具书之一。")]),_._v(" "),v("p",[_._v("赵祖康感于词条只定名而无释义，不能满足读者的要求，于是从1978年开始，组织力量，编了《道路与交通工程词典》，其难度较前者更大，共用了5年时间，才告完成。该书共有词目7\n100余条，120余万字，于1985年由人民交通出版社出版。出书以后深受道路及交通工程学者的欢迎。土木工程界前辈茅以升先生曾给以很高的评价。")]),_._v(" "),v("p",[_._v("赵祖康一生致力于道路交通术语规范化的研究，并作出了突出的贡献。他在长期从事公路工程实践中，对公路定线有着较深的研究。他在编写的“公路定线之研究”中，认为定线有三个主要控制因素，即交通量、地形、造价，因为三个字的英文，都含有“T”字母（Traffic，Topograph，Cost），所以简称为3“T”因素；四个定线目标即：速度、安全、经济、景观，因为四个字的英文名称（Speed，Safety，Saving，Scenic），都以“S”字母为首，故又称为4“S”目标；五个定线要目即：距离、坡度、线形、宽度、排水；五个山岭线即：越岭线、山腹线、山脚线、山谷线、岭脊线；还有二个主要定线方法，即：纸上定线、实地定线。考虑周详，提要钩玄，对公路定线很有指导意义。")]),_._v(" "),v("p",[_._v("赵祖康作为一位道路工程专家，1986年的《上海交通大学通讯》曾介绍说：“路从他脚下延伸”。作为一位市政工程专家，1989年，当他90寿辰时，当时任上海市市长的朱镕基曾亲往祝贺，称他：“年高德劭，功在上海”。他的道德学问，受到人们的尊敬。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_4YfKqPxz_4xO3d97g2_1WA==_3975271096101766144.jpg",alt:""}}),_._v("人物书籍")]),_._v(" "),v("p",[_._v("1 赵祖康等.英汉道路工程词汇.北京：人民交通出版社，1965.")]),_._v(" "),v("p",[_._v("2 赵祖康等.道路与交通工程词典.北京：人民交通出版社，1985.")]),_._v(" "),v("p",[_._v("3 赵祖康.公路名词译订法之研究.土木工程师学会年会论文，1928.")]),_._v(" "),v("p",[_._v("4 赵祖康等.公路路线规范草案.国民政府交通部技术标准委员会，1946.")]),_._v(" "),v("p",[_._v("5 赵祖康.道路曲线测设简法.道路协会，1929.")]),_._v(" "),v("p",[_._v("6 赵祖康等.上海居民区发展概论.联合国亚太地区地方政府发展人类居住区会议论文，1982.")]),_._v(" "),v("p",[_._v("7 赵祖康.浅谈上海城市总体规划.上海市容环卫报，1984（4）.")]),_._v(" "),v("p",[_._v("8 赵祖康等.三十年来之公路·三十年来中国工程.中国工程师学会出版，1946.")]),_._v(" "),v("p",[_._v("9 赵祖康.全国经济委员会第一试验路报告.第七次国际道路会议论文，1934.")]),_._v(" "),v("p",[_._v("---|---")])])}),[],!1,null,null,null);v.default=a.exports}}]);